import React, { useState } from 'react';
import '../../App.css';

const EditarPerfil = () => {
  const [perfil, setPerfil] = useState({
    rut: '15452684-9',
    nombre: 'PERSONA1',
    apellido: 'APELLIDO1',
    numero: '974859214',
    email: 'persona1@email.com',
  });

  const [editando, setEditando] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerfil(prevPerfil => ({
      ...prevPerfil,
      [name]: value,
    }));
  };

  const handleGuardarCambios = () => {
    console.log('Perfil actualizado:', perfil);
    setEditando(false);
  };

  return (
    <div className='profile-container'>
      <div className='profile-header'>
        <div className='profile-avatar'>
          {perfil.nombre.charAt(0)}{perfil.apellido.charAt(0)}
        </div>
        <h2 className='profile-name'>{perfil.nombre} {perfil.apellido}</h2>
      </div>
      <div className='profile-content'>
        {!editando ? (
          <>
            <div className='profile-info'>
              <p><strong>RUT:</strong> {perfil.rut}</p>
              <p><strong>Teléfono:</strong> {perfil.numero}</p>
              <p><strong>Email:</strong> {perfil.email}</p>
            </div>
            <button className='profile-button edit' onClick={() => setEditando(true)}>Editar Perfil</button>
          </>
        ) : (
          <form className='profile-form'>
            <div className='form-group'>
              <label>Nombre:</label>
              <input
                type="text"
                name="nombre"
                value={perfil.nombre}
                onChange={handleInputChange}
              />
            </div>
            <div className='form-group'>
              <label>Apellido:</label>
              <input
                type="text"
                name="apellido"
                value={perfil.apellido}
                onChange={handleInputChange}
              />
            </div>
            <div className='form-group'>
              <label>Teléfono:</label>
              <input
                type="text"
                name="numero"
                value={perfil.numero}
                onChange={handleInputChange}
              />
            </div>
            <div className='form-group'>
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={perfil.email}
                onChange={handleInputChange}
              />
            </div>
            <div className='form-actions'>
              <button className='profile-button save' onClick={handleGuardarCambios}>Guardar</button>
              <button className='profile-button cancel' onClick={() => setEditando(false)}>Cancelar</button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default EditarPerfil;
