import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../context/authContext';
import '../../App.css';

const mockAssignments = [
  { 
    id: 'A105', 
    conductor: { rut: '17665368-9', nombre: 'Juan Pérez' }, 
    pasajeros: [
      { rut: '19234567-8', nombre: 'Ana García' },
      { rut: '18345678-9', nombre: 'Carlos López' }
    ], 
    detalles: 'Detalles de la asignación A105' 
  },
  { 
    id: 'A106', 
    conductor: { rut: '17800268-9', nombre: 'María Rodríguez' }, 
    pasajeros: [
      { rut: '17456789-0', nombre: 'Pedro Sánchez' },
      { rut: '16567890-1', nombre: 'Laura Martínez' }
    ], 
    detalles: 'Detalles de la asignación A106' 
  },
  { 
    id: 'A107', 
    conductor: { rut: '17859268-9', nombre: 'José Fernández' }, 
    pasajeros: [
      { rut: '15678901-2', nombre: 'Carmen Díaz' },
      { rut: '14789012-3', nombre: 'Miguel Ángel Torres' }
    ], 
    detalles: 'Detalles de la asignación A107' 
  },
];

const mockPassengers = [
  { rut: '19234567-8', nombre: 'Ana García' },
  { rut: '18345678-9', nombre: 'Carlos López' },
  { rut: '17456789-0', nombre: 'Pedro Sánchez' },
  { rut: '16567890-1', nombre: 'Laura Martínez' },
  { rut: '15678901-2', nombre: 'Carmen Díaz' },
  { rut: '14789012-3', nombre: 'Miguel Ángel Torres' },
  { rut: '13890123-4', nombre: 'Isabel Ruiz' },
  { rut: '12901234-5', nombre: 'Francisco Morales' },
  { rut: '11012345-6', nombre: 'Sofía Vega' },
  { rut: '10123456-7', nombre: 'Javier Molina' }
];

const mockDrivers = [
  { rut: '17665368-9', nombre: 'Juan Pérez' },
  { rut: '17800268-9', nombre: 'María Rodríguez' },
  { rut: '17859268-9', nombre: 'José Fernández' }
];

const MAX_PASSENGERS = 4;

const GestionAsignaciones = () => {
  const { setAuth } = useContext(AuthContext);
  const [assignments, setAssignments] = useState(mockAssignments);
  const [showAllAssignments, setShowAllAssignments] = useState(false);
  const [selectedAssignment, setSelectedAssignment] = useState(null);
  const initialDisplayCount = 3;

  const [newAssignment, setNewAssignment] = useState({
    id: `A${Date.now().toString(36) + Math.random().toString(36).substr(2, 5)}`,
    conductor: '',
    pasajeros: Array(MAX_PASSENGERS).fill('')
  });

  const [updateAssignment, setUpdateAssignment] = useState({
    id: '',
    conductor: '',
    pasajeros: Array(MAX_PASSENGERS).fill('')
  });

  useEffect(() => {
    if (updateAssignment.id) {
      const selectedAssignment = assignments.find(a => a.id === updateAssignment.id);
      if (selectedAssignment) {
        setUpdateAssignment({
          ...updateAssignment,
          conductor: selectedAssignment.conductor.rut,
          pasajeros: selectedAssignment.pasajeros.map(p => p.rut)
        });
      }
    }
  }, [updateAssignment.id, assignments]);

  const styles = {
    // ... (mantén los estilos que ya tenías)
  };

  const handleNewPassengerChange = (index, value) => {
    const newPassengers = [...newAssignment.pasajeros];
    newPassengers[index] = value;
    setNewAssignment({
      ...newAssignment,
      pasajeros: newPassengers
    });
  };

  const handleUpdatePassengerChange = (index, value) => {
    const newPassengers = [...updateAssignment.pasajeros];
    newPassengers[index] = value;
    setUpdateAssignment({
      ...updateAssignment,
      pasajeros: newPassengers
    });
  };

  const handleSubmitNew = (e) => {
    e.preventDefault();
    const filteredPassengers = newAssignment.pasajeros.filter(p => p !== '');
    const newAssignmentData = {
      ...newAssignment,
      conductor: mockDrivers.find(d => d.rut === newAssignment.conductor),
      pasajeros: filteredPassengers.map(p => mockPassengers.find(mp => mp.rut === p))
    };
    setAssignments([...assignments, newAssignmentData]);
    setNewAssignment({
      id: `A${Date.now().toString(36) + Math.random().toString(36).substr(2, 5)}`,
      conductor: '',
      pasajeros: Array(MAX_PASSENGERS).fill('')
    });
  };

  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const filteredPassengers = updateAssignment.pasajeros.filter(p => p !== '');
    const updatedAssignmentData = {
      ...updateAssignment,
      conductor: mockDrivers.find(d => d.rut === updateAssignment.conductor),
      pasajeros: filteredPassengers.map(p => mockPassengers.find(mp => mp.rut === p))
    };
    const updatedAssignments = assignments.map((assignment) => {
      if (assignment.id === updatedAssignmentData.id) {
        return updatedAssignmentData;
      }
      return assignment;
    });
    setAssignments(updatedAssignments);
    setSelectedAssignment(null);
    setUpdateAssignment({
      id: '',
      conductor: '',
      pasajeros: Array(MAX_PASSENGERS).fill('')
    });
  };

  const handleShowAllAssignments = () => {
    setShowAllAssignments(!showAllAssignments);
  };

  const handleSelectAssignment = (assignment) => {
    setSelectedAssignment(assignment);
  };

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>Asignaciones</h2>
        {assignments.slice(0, showAllAssignments ? assignments.length : initialDisplayCount).map((assignment) => (
          <div key={assignment.id} style={styles.assignmentItem}>
            <span>{assignment.id}</span>
            <span>{assignment.conductor?.nombre || 'Sin conductor'}</span>
            <span>{assignment.pasajeros?.length || 0} pasajeros</span>
            <button style={styles.button} onClick={() => handleSelectAssignment(assignment)}>Ver detalles</button>
          </div>
        ))}
        {assignments.length > initialDisplayCount && (
          <button style={styles.button} onClick={handleShowAllAssignments}>
            {showAllAssignments ? 'Mostrar menos' : 'Mostrar todas'}
          </button>
        )}
      </section>

      {selectedAssignment && (
        <section className='user-section'>
           <h2 className='user-title'>Detalles de la asignación {selectedAssignment.id}</h2>
          <p>Conductor: {selectedAssignment.conductor?.nombre || 'Sin conductor'} ({selectedAssignment.conductor?.rut || ''})</p>
          <p>Pasajeros:</p>
          <ul>
            {selectedAssignment.pasajeros?.map((pasajero) => (
              <li key={pasajero.rut}>{pasajero.nombre} ({pasajero.rut})</li>
            ))}
          </ul>
        </section>
      )}

      <section className='user-section'>
        <h2 className='user-title'>Actualizar Asignación</h2>
        <form className='user-form' onSubmit={handleSubmitUpdate}>
          <label style={styles.label}>ID de la asignación:</label>
          <select 
            className='user-select' 
            value={updateAssignment.id} 
            onChange={(e) => setUpdateAssignment({ ...updateAssignment, id: e.target.value })}
          >
            <option value="">Seleccione una asignación</option>
            {assignments.map((assignment) => (
              <option key={assignment.id} value={assignment.id}>
                {assignment.id}
              </option>
            ))}
          </select>

          <label style={styles.label}>Conductor:</label>
          <select 
            className='user-select' 
            value={updateAssignment.conductor} 
            onChange={(e) => setUpdateAssignment({ ...updateAssignment, conductor: e.target.value })}
          >
            <option value="">Seleccione un conductor</option>
            {mockDrivers.map((driver) => (
              <option key={driver.rut} value={driver.rut}>{driver.nombre}</option>
            ))}
          </select>

          <div style={styles.passengerSection}>
            <label style={styles.label}>Pasajeros:</label>
            {Array(MAX_PASSENGERS).fill(0).map((_, index) => (
              <select 
                key={index} 
                style={styles.select} 
                value={updateAssignment.pasajeros[index] || ''} 
                onChange={(e) => handleUpdatePassengerChange(index, e.target.value)}
              >
                <option value="">Seleccione un pasajero</option>
                {mockPassengers.map((passenger) => (
                  <option key={passenger.rut} value={passenger.rut}>{passenger.nombre}</option>
                ))}
              </select>
            ))}
          </div>
          <button className='user-button' type="submit">Actualizar Asignación</button>
        </form>
      </section>

      <h2>Crear Asignación</h2>
      <section style={styles.section}>
        <form style={styles.form} onSubmit={handleSubmitNew}>
          <label style={styles.label}>Conductor:</label>
          <select style={styles.select} value={newAssignment.conductor} onChange={(e) => setNewAssignment({ ...newAssignment, conductor: e.target.value })}>
            <option value="">Seleccione un conductor</option>
            {mockDrivers.map((driver) => (
              <option key={driver.rut} value={driver.rut}>{driver.nombre}</option>
            ))}
          </select>
          <div style={styles.passengerSection}>
            <label style={styles.label}>Pasajeros:</label>
            {Array(MAX_PASSENGERS).fill(0).map((_, index) => (
              <select key={index} style={styles.select} value={newAssignment.pasajeros[index]} onChange={(e) => handleNewPassengerChange(index, e.target.value)}>
                <option value="">Seleccione un pasajero</option>
                {mockPassengers.map((passenger) => (
                  <option key={passenger.rut} value={passenger.rut}>{passenger.nombre}</option>
                ))}
              </select>
            ))}
          </div>
          <button style={styles.button} type="submit">Crear Asignación</button>
        </form>
      </section>
    </div>
  );
};

export default GestionAsignaciones;
