import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import Logo from '../logo';

const GestionConductor = () => {
  const { setAuth } = useContext(AuthContext);
  const [conductores, setConductores] = useState([
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '98632356', estado: 'ACTIVO' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '985468970', estado: 'NO ACTIVO' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '984461863', estado: 'NO ACTIVO' },
  ]);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    navigate(path); // Redirige a la ruta que corresponda
  };

  const handleLogout = () => {
    setAuth(null);
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
    },
    title: {
      backgroundColor: '#4a5d23',
      color: 'white',
      padding: '10px',
      textAlign: 'center',
    },
    section: {
      marginBottom: '20px',
      padding: '15px',
      borderRadius: '5px',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
    },
    th: {
      backgroundColor: '#f2f2f2',
      border: '1px solid #ddd',
      padding: '8px',
      textAlign: 'left',
    },
    td: {
      border: '1px solid #ddd',
      padding: '8px',
    },
    input: {
      width: '100%',
      padding: '5px',
      marginBottom: '10px',
    },
    button: {
      backgroundColor: '#4a5d23',
      color: 'white',
      border: 'none',
      padding: '10px 15px',
      cursor: 'pointer',
    },
  };

  return (
    <div>
      < Logo/>
    <div style={styles.container}>
      
      <h1 style={styles.title}>GESTIÓN DE CONDUCTOR</h1>

      <div style={{...styles.section, backgroundColor: '#ffe4b5'}}>
        <h2>CONDUCTORES REGISTRADOS</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>RUT</th>
              <th style={styles.th}>NOMBRE</th>
              <th style={styles.th}>NÚMERO</th>
              <th style={styles.th}>ESTADO</th>
            </tr>
          </thead>
          <tbody>
            {conductores.map((conductor) => (
              <tr key={conductor.rut}>
                <td style={styles.td}>{conductor.rut}</td>
                <td style={styles.td}>{conductor.nombre}</td>
                <td style={styles.td}>{conductor.numero}</td>
                <td style={styles.td}>{conductor.estado}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button style={styles.button}>Ver +</button>
      </div>

      <div style={{...styles.section, backgroundColor: '#e6f3e6'}}>
        <h2>AGREGAR CONDUCTORES</h2>
        <input style={styles.input} type="text" placeholder="RUT" />
        <input style={styles.input} type="text" placeholder="NOMBRE" />
        <input style={styles.input} type="text" placeholder="ROL" />
        <input style={styles.input} type="text" placeholder="NÚMERO" />
        <input style={styles.input} type="password" placeholder="CONTRASEÑA" />
        <button style={styles.button}>Agregar conductores</button>
      </div>

      <div style={{...styles.section, backgroundColor: '#f0f0f0'}}>
        <h2>ACTUALIZAR CONDUCTOR</h2>
        <input style={styles.input} type="text" placeholder="RUT" />
        <input style={styles.input} type="text" placeholder="NOMBRE" />
        <input style={styles.input} type="text" placeholder="NÚMERO" />
        <button style={styles.button}>Actualizar conductores</button>
      </div>

      <div style={{...styles.section, backgroundColor: '#e6f3e6'}}>
        <h2>ELIMINAR CONDUCTOR</h2>
        <input style={styles.input} type="text" placeholder="RUT DEL CONDUCTOR" />
        <textarea style={styles.input} placeholder="DATOS DEL CONDUCTOR" rows={4}></textarea>
        <button style={styles.button}>Buscar</button>
        <button style={styles.button}>Eliminar conductor</button>
      </div>

      <div style={{...styles.section, backgroundColor: '#f0f0f0'}}>
        <h2>ACTIVAR O DESACTIVAR A CONDUCTOR</h2>
        <input style={styles.input} type="text" placeholder="RUT DEL CONDUCTOR" />
        <select style={styles.input}>
          <option value="">ESTADO</option>
          <option value="activo">Activo</option>
          <option value="inactivo">Inactivo</option>
        </select>
        <button style={styles.button}>Buscar</button>
        <button style={styles.button}>Actualizar Estado</button>
      </div>

      <button style={{...styles.button, display: 'block', margin: '20px auto'}} onClick={() => handleNavigation('/admin')}>
        VOLVER AL PANEL
      </button>
    </div>
    </div>
  );
};

export default GestionConductor;
