import React, { useState } from 'react';

const Incidencias = () => {
  const [incidencias, setIncidencias] = useState([]);
  const [descripcion, setDescripcion] = useState('');

  const handleAgregarIncidencia = () => {
    if (descripcion.trim()) {
      setIncidencias([...incidencias, descripcion]);
      setDescripcion('');
    }
  };

  return (
    <div className='incidencias-container'>
      <h3>Incidencias</h3>
      <div className='incidencias-form'>
        <textarea
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          placeholder='DescripciÃ³n de la incidencia'
          rows='4'
          style={{ width: '100%', marginBottom: '10px' }}
        />
        <button className='profile-button' onClick={handleAgregarIncidencia}>
          Agregar Incidencia
        </button>
      </div>
      <h4>Lista de Incidencias</h4>
      <ul>
        {incidencias.map((incidencia, index) => (
          <li key={index}>{incidencia}</li>
        ))}
      </ul>
    </div>
  );
};

export default Incidencias;
