import React, { useContext } from 'react';
import { AuthContext } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import Logo from '../logo';
import '../../App.css'; // Para mover los estilos al archivo CSS

const BienvenidoConductor = () => {
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    setAuth(null);
    navigate('/'); // Redirige a la página de inicio
  };

  const handleNavigation = (path) => {
    navigate(path); // Redirige a la ruta correspondiente
  };

  return (
    <div>
      <Logo />
      <div className="conductor-container">
        <h1 className="conductor-title">BIENVENIDO CONDUCTOR</h1>
        <div className="options-container">
          <h2 className="question">¿QUÉ DESEAS HACER?</h2>
          <button
            type="button"
            className="conductor-button editar-perfil"
            onClick={() => handleNavigation('/perfil')}
          >
            Editar Perfil
          </button>
          <button
            type="button"
            className="conductor-button ruta"
            onClick={() => handleNavigation('/ruta')}
          >
            Ruta
          </button>
          <button
            type="button"
            className="conductor-button incidencia"
            onClick={() => handleNavigation('/incidencia')}
          >
            Incidencia
          </button>
        </div>
        <button className="cerrar-sesion" onClick={handleLogout}>
          CERRAR SESIÓN
        </button>
      </div>
    </div>
  );
};

export default BienvenidoConductor;
