import React, { useState } from 'react';
import '../../App.css';

const VerRuta = () => {
  const [rutas, setRutas] = useState([
    { idRuta: 'R105', idAsignacion: 'A105', nombrePasajero: 'PERSONA 1', direccion: 'AV 105', telefono: '123456789' },
    { idRuta: 'R105', idAsignacion: 'A107', nombrePasajero: 'PERSONA 2', direccion: 'AV 205', telefono: '987654321' },
    { idRuta: 'R105', idAsignacion: 'A104', nombrePasajero: 'PERSONA 3', direccion: 'AV 109', telefono: '456789123' },
    { idRuta: 'R105', idAsignacion: 'A112', nombrePasajero: 'PERSONA 4', direccion: 'AV 107', telefono: '789123456' },
  ]);

  const [selectedRuta, setSelectedRuta] = useState(null);

  const handleVerDetalles = (ruta) => {
    setSelectedRuta(ruta);
  };

  const handleCerrarDetalles = () => {
    setSelectedRuta(null);
  };

  const abrirMapa = (direccion) => {
    const url = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(direccion)}`;
    window.open(url, '_blank');
  };

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>RUTA</h2>
        <div className='user-info'>
          <p>MI RUTA INICIA A LAS 7:00 Y TERMINA A LAS 8:00</p>
        </div>
        
        <table className='user-table'>
          <thead>
            <tr>
              <th className='user-th'>ID RUTA</th>
              <th className='user-th'>ID ASIGNACIÓN</th>
              <th className='user-th'>NOMBRE PASAJERO</th>
              <th className='user-th'>DIRECCIÓN</th>
              <th className='user-th'>ACCIÓN</th>
            </tr>
          </thead>
          <tbody>
            {rutas.map((ruta, index) => (
              <tr key={index}>
                <td className='user-td'>{ruta.idRuta}</td>
                <td className='user-td'>{ruta.idAsignacion}</td>
                <td className='user-td'>{ruta.nombrePasajero}</td>
                <td className='user-td'>{ruta.direccion}</td>
                <td className='user-td'>
                  <button className='user-button' onClick={() => handleVerDetalles(ruta)}>Ver + Detalles</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Lista para dispositivos móviles */}
        <ul className='mobile-list'>
          {rutas.map((ruta, index) => (
            <li key={index} className='mobile-item'>
              <div className='mobile-item-field'><span className='mobile-item-label'>ID RUTA:</span> {ruta.idRuta}</div>
              <div className='mobile-item-field'><span className='mobile-item-label'>ID ASIGNACIÓN:</span> {ruta.idAsignacion}</div>
              <div className='mobile-item-field'><span className='mobile-item-label'>NOMBRE PASAJERO:</span> {ruta.nombrePasajero}</div>
              <div className='mobile-item-field'><span className='mobile-item-label'>DIRECCIÓN:</span> {ruta.direccion}</div>
              <button className='user-button' onClick={() => handleVerDetalles(ruta)}>Ver + Detalles</button>
            </li>
          ))}
        </ul>
      </section>

      {selectedRuta && (
        <div className='modal'>
          <div className='modal-content'>
            <h3>Detalles del Pasajero</h3>
            <p><strong>Nombre:</strong> {selectedRuta.nombrePasajero}</p>
            <p><strong>Dirección:</strong> {selectedRuta.direccion}</p>
            <p><strong>Teléfono:</strong> {selectedRuta.telefono}</p>
            <button className='user-button' onClick={() => abrirMapa(selectedRuta.direccion)}>Ver en Google Maps</button>
            <button className='user-button' onClick={handleCerrarDetalles}>Cerrar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default VerRuta;
