import React, { useState, useEffect } from 'react';
import api from './api';

// Simulated API functions (replace these with actual API calls)
const fetchDrivers = () => {
  // Simulated API call to fetch drivers
  return Promise.resolve([
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '986432256', estado: 'ACTIVO' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '985468970', estado: 'NO ACTIVO' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '984461863', estado: 'NO ACTIVO' },
  ]);
};

const addDriver = (driver) => {
  // Simulated API call to add a driver
  return Promise.resolve({ ...driver, estado: 'ACTIVO' });
};

const updateDriver = (rut, updates) => {
  // Simulated API call to update a driver
  return Promise.resolve({ rut, ...updates });
};

const deleteDriver = (rut) => {
  // Simulated API call to delete a driver
  return Promise.resolve({ success: true });
};

const toggleDriverStatus = (rut, newStatus) => {
  // Simulated API call to toggle driver status
  return Promise.resolve({ rut, estado: newStatus });
};

const DriverManagement = () => {
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState({ rut: '', nombre: '', numero: '' });
  const [updateDriverData, setUpdateDriverData] = useState({ rut: '', nombre: '', numero: '' });
  const [deleteDriverData, setDeleteDriverData] = useState({ rut: '', driverInfo: null });
  const [activateDriverData, setActivateDriverData] = useState({ rut: '', estado: '' });

  useEffect(() => {
    fetchDrivers().then(setDrivers);
  }, []);

  const handleAddDriver = async (e) => {
    e.preventDefault();
    const addedDriver = await addDriver(newDriver);
    setDrivers([...drivers, addedDriver]);
    setNewDriver({ rut: '', nombre: '', numero: '' });
  };

  const handleUpdateDriver = async (e) => {
    e.preventDefault();
    const { rut, ...updates } = updateDriverData;
    const updatedDriver = await updateDriver(rut, updates);
    setDrivers(drivers.map(d => d.rut === rut ? { ...d, ...updatedDriver } : d));
    setUpdateDriverData({ rut: '', nombre: '', numero: '' });
  };

  const handleDeleteSearch = async (e) => {
    e.preventDefault();
    const driverInfo = drivers.find(d => d.rut === deleteDriverData.rut);
    setDeleteDriverData({ ...deleteDriverData, driverInfo });
  };

  const handleDeleteDriver = async () => {
    await deleteDriver(deleteDriverData.rut);
    setDrivers(drivers.filter(d => d.rut !== deleteDriverData.rut));
    setDeleteDriverData({ rut: '', driverInfo: null });
  };

  const handleActivateSearch = async (e) => {
    e.preventDefault();
    const driver = drivers.find(d => d.rut === activateDriverData.rut);
    if (driver) {
      setActivateDriverData({ ...activateDriverData, estado: driver.estado });
    }
  };

  const handleToggleStatus = async () => {
    const newStatus = activateDriverData.estado === 'ACTIVO' ? 'NO ACTIVO' : 'ACTIVO';
    try {
      const response = await api.patch(`/drivers/${activateDriverData.rut}/status`, { estado: newStatus });
      setDrivers(drivers.map(d => d.rut === response.data.rut ? response.data : d));
      setActivateDriverData({ rut: '', estado: '' });
    } catch (error) {
      console.error('Error toggling driver status:', error);
    }
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
    },
    section: {
      backgroundColor: '#e6f3e6',
      padding: '20px',
      marginBottom: '20px',
      borderRadius: '5px',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
    },
    th: {
      backgroundColor: '#c1e0c1',
      padding: '10px',
      textAlign: 'left',
    },
    td: {
      padding: '10px',
      borderBottom: '1px solid #c1e0c1',
    },
    form: {
      display: 'grid',
      gap: '10px',
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    },
    input: {
      width: '100%',
      padding: '5px',
      boxSizing: 'border-box',
    },
    button: {
      backgroundColor: '#4CAF50',
      color: 'white',
      padding: '10px',
      border: 'none',
      cursor: 'pointer',
      borderRadius: '5px',
    },
    h2: {
      color: '#2e7d32',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.section}>
        <h2 style={styles.h2}>CONDUCTORES REGISTRADOS</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>RUT</th>
              <th style={styles.th}>NOMBRE</th>
              <th style={styles.th}>NÚMERO</th>
              <th style={styles.th}>ESTADO</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map((driver) => (
              <tr key={driver.rut}>
                <td style={styles.td}>{driver.rut}</td>
                <td style={styles.td}>{driver.nombre}</td>
                <td style={styles.td}>{driver.numero}</td>
                <td style={styles.td}>{driver.estado}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>AGREGAR CONDUCTORES</h2>
        <form onSubmit={handleAddDriver} style={styles.form}>
          <input
            type="text"
            placeholder="RUT"
            value={newDriver.rut}
            onChange={(e) => setNewDriver({ ...newDriver, rut: e.target.value })}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="NOMBRE"
            value={newDriver.nombre}
            onChange={(e) => setNewDriver({ ...newDriver, nombre: e.target.value })}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="NÚMERO"
            value={newDriver.numero}
            onChange={(e) => setNewDriver({ ...newDriver, numero: e.target.value })}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Agregar conductor</button>
        </form>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>ACTUALIZAR CONDUCTOR</h2>
        <form onSubmit={handleUpdateDriver} style={styles.form}>
          <input
            type="text"
            placeholder="RUT"
            value={updateDriverData.rut}
            onChange={(e) => setUpdateDriverData({ ...updateDriverData, rut: e.target.value })}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="NOMBRE"
            value={updateDriverData.nombre}
            onChange={(e) => setUpdateDriverData({ ...updateDriverData, nombre: e.target.value })}
            style={styles.input}
            required
          />
          <input
            type="text"
            placeholder="NÚMERO"
            value={updateDriverData.numero}
            onChange={(e) => setUpdateDriverData({ ...updateDriverData, numero: e.target.value })}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Actualizar conductor</button>
        </form>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>ELIMINAR CONDUCTOR</h2>
        <form onSubmit={handleDeleteSearch} style={styles.form}>
          <input
            type="text"
            placeholder="RUT DEL CONDUCTOR"
            value={deleteDriverData.rut}
            onChange={(e) => setDeleteDriverData({ ...deleteDriverData, rut: e.target.value })}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Buscar</button>
        </form>
        {deleteDriverData.driverInfo && (
          <div style={{ marginTop: '20px' }}>
            <p>RUT: {deleteDriverData.driverInfo.rut}</p>
            <p>Nombre: {deleteDriverData.driverInfo.nombre}</p>
            <p>Número: {deleteDriverData.driverInfo.numero}</p>
            <p>Estado: {deleteDriverData.driverInfo.estado}</p>
            <button onClick={handleDeleteDriver} style={styles.button}>Eliminar conductor</button>
          </div>
        )}
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>ACTIVAR O DESACTIVAR A CONDUCTOR</h2>
        <form onSubmit={handleActivateSearch} style={styles.form}>
          <input
            type="text"
            placeholder="RUT DEL CONDUCTOR"
            value={activateDriverData.rut}
            onChange={(e) => setActivateDriverData({ ...activateDriverData, rut: e.target.value })}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Buscar</button>
        </form>
        {activateDriverData.estado && (
          <div style={{ marginTop: '20px' }}>
            <p>Estado actual: {activateDriverData.estado}</p>
            <button onClick={handleToggleStatus} style={styles.button}>
              {activateDriverData.estado === 'ACTIVO' ? 'Desactivar' : 'Activar'} conductor
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default DriverManagement;
