// api.js
import axios from 'axios';

let API_URL;

if (__DEV__) {
  API_URL = 'http://localhost:3000/api';
  // API_URL = 'http://10.0.2.2:3000/api';  // Usa esta línea si estás desarrollando con un emulador Android
} else {
  API_URL = 'https://tu-api-produccion.com/api';
}

const api = axios.create({
  baseURL: API_URL,
});

export default api;
