import React, { useState } from 'react';
import { Menu, Users, MapPin, AlertTriangle, FileSpreadsheet, Car, LogOut } from 'lucide-react';

const menuItems = [
  { name: 'Conductor', icon: Users },
  { name: 'Pasajero', icon: Users },
  { name: 'Rutas', icon: MapPin },
  { name: 'Incidencias', icon: AlertTriangle },
  { name: 'Asignación', icon: FileSpreadsheet },
  { name: 'Auto', icon: Car },
];

const conductores = [
  { rut: '17665368-9', nombre: 'PERSONA 1', numero: '986432156', estado: 'ACTIVO' },
  { rut: '17800268-9', nombre: 'PERSONA 1', numero: '985466970', estado: 'NO ACTIVO' },
  { rut: '17859268-9', nombre: 'PERSONA 1', numero: '984461863', estado: 'NO ACTIVO' },
];

const pasajeros = [
  { rut: '17665368-9', nombre: 'PERSONA 1', numero: '', estado: '' },
  { rut: '17800268-9', nombre: 'PERSONA 2', numero: '', estado: '' },
  { rut: '17859268-9', nombre: 'PERSONA 3', numero: '', estado: '' },
];

function App() {
    const [selectedMenu, setSelectedMenu] = useState('Conductor');
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const renderTable = () => {
    const data = selectedMenu === 'Conductor' ? conductores : pasajeros;
    return (
        <div className="app">
          <header className="header">
            <h1>PANEL DE CONTROL</h1>
            <button
              className="mobile-menu-button"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            >
              <Menu />
            </button>
          </header>
          <div className="content">
            <nav className={`sidebar ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
              <div className="menu-items">
                {menuItems.map((item) => (
                  <button
                    key={item.name}
                    className={`menu-item ${selectedMenu === item.name ? 'active' : ''}`}
                    onClick={() => setSelectedMenu(item.name)}
                  >
                    <item.icon size={20} />
                    <span>{item.name}</span>
                  </button>
                ))}
              </div>
              <button className="logout-button">
                <LogOut size={20} />
                <span>CERRAR SESIÓN</span>
              </button>
            </nav>
            <main className="main-content">
              <h2>{selectedMenu}</h2>
              {(selectedMenu === 'Conductor' || selectedMenu === 'Pasajero') && renderTable()}
            </main>
          </div>
        </div>
      );
    }
}   
    export default App;
