import React, { useState, useEffect } from 'react';
import '../../App.css';

// Simular llamadas a la API
const fetchPassengers = () => {
  return Promise.resolve([
    { rut: '14665368-9', nombre: 'Tomas', apellido:'Lopez', direccion: 'Av. Grecia', numero: '986452256', estado: 'ACTIVO' },
    { rut: '17800268-2', nombre: 'Katherine',apellido:'Molina', direccion: 'Av. Granaderos', numero: '955468970', estado: 'NO ACTIVO' },
    // ... otros pasajeros
  ]);
};

const fetchDrivers = () => {
  return Promise.resolve([
    { rut: '17665368-9', nombre: 'Jorge', apellido:'Guerrero', numero: '986432256', vehiculo: 'ABCD30', estado: 'ACTIVO' },
    { rut: '15800268-4', nombre: 'Antonia', apellido:'Gomez', numero: '955468970', vehiculo: 'AACD15', estado: 'NO ACTIVO' },
    // ... otros conductores
  ]);
};

const fetchVehicles = () => {
  return Promise.resolve([
    { patente: 'ABCD30', marca: 'Toyota', modelo: 'Corolla', año: 2020, conductor: 'Jorge Guerrero' },
    { patente: 'AACD15', marca: 'Honda', modelo: 'Civic', año: 2019, conductor: 'Antonia Gomez' },
    // ... otros vehículos
  ]);
};

const GestionRutas = () => {
  const [rutas, setRutas] = useState([]);
  const [conductores, setConductores] = useState([]);
  const [pasajeros, setPasajeros] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [rutaSeleccionada, setRutaSeleccionada] = useState(null);
  const [nuevaRuta, setNuevaRuta] = useState({
    id: '',
    asignacion: '',
    fecha: '',
    horaInicio: '',
    horaLlegada: '',
    conductorRut: '',
    pasajeroRuts: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const cargarDatos = async () => {
      try {
        const [pasajerosData, conductoresData, vehiculosData] = await Promise.all([
          fetchPassengers(),
          fetchDrivers(),
          fetchVehicles()
        ]);

        setPasajeros(pasajerosData);
        setConductores(conductoresData);
        setVehiculos(vehiculosData);

        // Inicializar rutas con datos completos
        const rutasIniciales = [
          {
            id: 'R105',
            asignacion: 'A001',
            fecha: '2023-07-01',
            horaInicio: '08:00',
            horaLlegada: '09:30',
            conductor: conductoresData[0],
            vehiculo: vehiculosData[0],
            pasajeros: [pasajerosData[0], pasajerosData[1]]
          },
          // ... más rutas iniciales si las necesitas
        ];

        setRutas(rutasIniciales);
        setLoading(false);
      } catch (error) {
        console.error('Error al cargar los datos:', error);
        setLoading(false);
      }
    };

    cargarDatos();
  }, []);

  const handleVerDetalles = (ruta) => {
    setRutaSeleccionada(ruta);
  };

  const handleCrearRuta = async (e) => {
    e.preventDefault();
    const conductor = conductores.find(c => c.rut === nuevaRuta.conductorRut);
    const vehiculo = vehiculos.find(v => v.patente === conductor?.vehiculo);
    const rutaPasajeros = nuevaRuta.pasajeroRuts
      .map(rut => pasajeros.find(p => p.rut === rut))
      .filter(p => p);

    const nuevaRutaCompleta = {
      ...nuevaRuta,
      conductor,
      vehiculo,
      pasajeros: rutaPasajeros,
      detalles: `Detalles de la ruta ${nuevaRuta.id}`
    };

    setRutas([...rutas, nuevaRutaCompleta]);
    setNuevaRuta({
      id: '',
      asignacion: '',
      fecha: '',
      horaInicio: '',
      horaLlegada: '',
      conductorRut: '',
      pasajeroRuts: []
    });
  };

  if (loading) {
    return <div>Cargando...</div>;
  }

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>Ver Rutas</h2>
        <table className='user-table'>
          <thead>
            <tr>
              <th className='user-th'>ID</th>
              <th className='user-th'>Asignación</th>
              <th className='user-th'>Conductor</th>
              <th className='user-th'>Vehículo</th>
              <th className='user-th'>Fecha</th>
              <th className='user-th'>Hora Inicio</th>
              <th className='user-th'>Hora Llegada</th>
              <th className='user-th'>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {rutas.map((ruta) => (
              <tr key={ruta.id}>
                <td className='user-td'>{ruta.id}</td>
                <td className='user-td'>{ruta.asignacion}</td>
                <td className='user-td'>{`${ruta.conductor.nombre} ${ruta.conductor.apellido}`}</td>
                <td className='user-td'>{ruta.vehiculo.patente}</td>
                <td className='user-td'>{ruta.fecha}</td>
                <td className='user-td'>{ruta.horaInicio}</td>
                <td className='user-td'>{ruta.horaLlegada}</td>
                <td className='user-td'>
                  <button className='user-button' onClick={() => handleVerDetalles(ruta)}>
                    Ver Detalles
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {rutaSeleccionada && (
          <div className='detalles-container'>
            <h3>Detalles de la Ruta</h3>
            <div className='conductor-info'>
              <h4>Conductor</h4>
              <p>Nombre: {rutaSeleccionada.conductor.nombre} {rutaSeleccionada.conductor.apellido}</p>
              <p>RUT: {rutaSeleccionada.conductor.rut}</p>
              <p>Número: {rutaSeleccionada.conductor.numero}</p>
            </div>
            <div className='vehiculo-info'>
              <h4>Vehículo</h4>
              <p>Patente: {rutaSeleccionada.vehiculo.patente}</p>
              <p>Marca: {rutaSeleccionada.vehiculo.marca}</p>
              <p>Modelo: {rutaSeleccionada.vehiculo.modelo}</p>
              <p>Año: {rutaSeleccionada.vehiculo.año}</p>
            </div>
            <div className='pasajeros-info '>
              <h4>Pasajeros</h4>
              <ul>
                {rutaSeleccionada.pasajeros.map((pasajero) => (
                  <li key={pasajero.rut}>
                    <p>Nombre: {pasajero.nombre} {pasajero.apellido}</p>
                    <p>RUT: {pasajero.rut}</p>
                    <p>Dirección: {pasajero.direccion}</p>
                    <p>Número: {pasajero.numero}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Crear Ruta</h2>
        <form className='user-form' onSubmit={handleCrearRuta}>
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID DE RUTA" 
            value={nuevaRuta.id}
            onChange={(e) => setNuevaRuta({...nuevaRuta, id: e.target.value})}
          />
          <input 
            className='user-input' 
            type="text" 
            placeholder="ASIGNACIÓN" 
            value={nuevaRuta.asignacion}
            onChange={(e) => setNuevaRuta({...nuevaRuta, asignacion: e.target.value})}
          />
          <input 
            className='user-input' 
            type="date" 
            placeholder="FECHA" 
            value={nuevaRuta.fecha}
            onChange={(e) => setNuevaRuta({...nuevaRuta, fecha: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA INICIO" 
            value={nuevaRuta.horaInicio}
            onChange={(e) => setNuevaRuta({...nuevaRuta, horaInicio: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA LLEGADA" 
            value={nuevaRuta.horaLlegada}
            onChange={(e) => setNuevaRuta({...nuevaRuta, horaLlegada: e.target.value})}
          />
          <select 
            className='user-input' 
            value={nuevaRuta.conductorRut}
            onChange={(e) => setNuevaRuta({...nuevaRuta, conductorRut: e.target.value})}
          >
            <option value="">Seleccione un conductor</option>
            {conductores.map((conductor) => (
              <option key={conductor.rut} value={conductor.rut}>
                {conductor.nombre} {conductor.apellido}
              </option>
            ))}
          </select>
          <select 
            className='user-input' 
            multiple
            value={nuevaRuta.pasajeroRuts}
            onChange={(e) => {
              const selectedRuts = Array.from(e.target.selectedOptions, (option) => option.value);
              setNuevaRuta({...nuevaRuta, pasajeroRuts: selectedRuts});
            }}
          >
            <option value="">Seleccione pasajeros</option>
            {pasajeros.map((pasajero) => (
              <option key={pasajero.rut} value={pasajero.rut}>
                {pasajero.nombre} {pasajero.apellido}
              </option>
            ))}
          </select>
          <button className='user-button' type="submit">Agregar ruta</button>
        </form>
      </section>

      {/* Resto del componente... */}
    </div>
  );
};

export default GestionRutas;
