import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../context/authContext';

// Simulated API functions (replace with actual API calls)
const fetchAssignments = () => {
  return Promise.resolve([
    { id: 'A105', conductor: '17665368-9', pasajeros: ['19234567-8', '18345678-9'], detalles: 'Detalles de la asignación A105' },
    { id: 'A106', conductor: '17800268-9', pasajeros: ['17456789-0', '16567890-1'], detalles: 'Detalles de la asignación A106' },
    { id: 'A107', conductor: '17859268-9', pasajeros: ['15678901-2', '14789012-3'], detalles: 'Detalles de la asignación A107' },
  ]);
};

const GestionAsignaciones = () => {
  const { setAuth } = useContext(AuthContext);
  const [assignments, setAssignments] = useState([]);
  const [drivers] = useState(['17665368-9', '17800268-9', '17859268-9']); // Simulated drivers list
  const [passengers] = useState(['19234567-8', '18345678-9', '17456789-0', '16567890-1', '15678901-2']); // Simulated passengers list
  
  const [newAssignment, setNewAssignment] = useState({ 
    id: `A${Math.floor(Math.random() * 900) + 100}`, // Genera un ID aleatorio
    conductor: '', 
    pasajeros: [] 
  });
  const [updateAssignment, setUpdateAssignment] = useState({ id: '', conductor: '', pasajeros: [] });
  const [deleteAssignment, setDeleteAssignment] = useState({ id: '', assignmentInfo: null });
  const [viewAssignment, setViewAssignment] = useState({ id: '', details: '' });

  useEffect(() => {
    fetchAssignments().then(setAssignments);
  }, []);

  // Función para manejar la selección múltiple de pasajeros
  const handlePassengerSelection = (e, setterFunction, currentState) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map(option => option.value);
    setterFunction({
      ...currentState,
      pasajeros: selectedOptions
    });
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
    },
    section: {
      backgroundColor: '#e6f3ff',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
      marginBottom: '20px',
    },
    h2: {
      fontSize: '18px',
      fontWeight: 'bold',
      marginBottom: '10px',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '10px',
    },
    select: {
      width: '100%',
      padding: '8px',
      boxSizing: 'border-box',
      minHeight: '35px',
      backgroundColor: 'white',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontSize: '14px',
    },
    multiSelect: {
      width: '100%',
      padding: '8px',
      boxSizing: 'border-box',
      minHeight: '100px', // Altura aumentada para selección múltiple
      backgroundColor: 'white',
      border: '1px solid #ccc',
      borderRadius: '4px', fontSize: '14px',
    },
    input: {
      width: '100%',
      padding: '8px',
      boxSizing: 'border-box',
      height: '35px',
      backgroundColor: 'white',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontSize: '14px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#4a5d23',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '14px',
    },
    textarea: {
      width: '100%',
      padding: '8px',
      minHeight: '100px',
      boxSizing: 'border-box',
      backgroundColor: 'white',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontSize: '14px',
    },
  };

  const handleLogout = () => {
    setAuth(null);
  };

  const handleNewAssignment = (e) => {
    e.preventDefault();
    // Simulated API call to create new assignment
    console.log('Crear asignación:', newAssignment);
  };

  const handleUpdateAssignment = (e) => {
    e.preventDefault();
    // Simulated API call to update assignment
    console.log('Actualizar asignación:', updateAssignment);
  };

  const handleDeleteAssignment = (e) => {
    e.preventDefault();
    // Simulated API call to delete assignment
    console.log('Eliminar asignación:', deleteAssignment);
  };

  const handleViewAssignment = (e) => {
    e.preventDefault();
    // Simulated API call to view assignment details
    console.log('Ver detalles de asignación:', viewAssignment);
  };

  return (
    <div style={styles.container}>
      <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '20px' }}>Gestión de Asignaciones</h1>

      <div style={styles.section}>
        <h2 style={styles.h2}>VER ASIGNACIÓN</h2>
        <form style={styles.form}>
          <select style={styles.select} value={viewAssignment.id} onChange={(e) => setViewAssignment({ id: e.target.value, details: '' })}>
            <option value="">Seleccione una asignación</option>
            {assignments.map((assignment) => (
              <option key={assignment.id} value={assignment.id}>{assignment.id}</option>
            ))}
          </select>
          <button style={styles.button} onClick={handleViewAssignment}>Ver detalles</button>
          <textarea style={styles.textarea} value={viewAssignment.details} readOnly></textarea>
        </form>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>BUSCAR ASIGNACIÓN</h2>
        <form style={styles.form}>
          <input style={styles.input} type="text" placeholder="ID ASIGNACIÓN" value={deleteAssignment.id} onChange={(e) => setDeleteAssignment({ id: e.target.value, assignmentInfo: null })} />
          <button style={styles.button} onClick={handleDeleteAssignment}>Buscar</button>
          <textarea style={styles.textarea} value={deleteAssignment.assignmentInfo} readOnly></textarea>
        </form>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>CREAR ASIGNACIÓN</h2>
        <form style={styles.form}>
          <input style={styles.input} type="text" placeholder="ID ASIGNACIÓN" value={newAssignment.id} disabled />
          <select style={styles.select} value={newAssignment.conductor} onChange={(e) => setNewAssignment({ ...newAssignment, conductor: e.target.value })}>
            <option value="">Seleccione un conductor</option>
            {drivers.map((driver) => (
              <option key={driver} value={driver}>{driver}</option>
            ))}
          </select>
          <select style={styles.multiSelect} multiple value={newAssignment.pasajeros} onChange={(e) => handlePassengerSelection(e, setNewAssignment, newAssignment)}>
            <option value="">Seleccione pasajeros</option>
            {passengers.map((passenger) => (
              <option key={passenger} value={passenger}>{passenger}</option>
            ))}
          </select>
          <button style={styles.button} onClick={handleNewAssignment}>Crear asignación</button>
        </form>
      </div>

      <div style={styles.section}>
        <h2 style={styles.h2}>MODIFICAR ASIGNACIÓN</h2>
        <form style={styles.form}>
        <input style={styles.input} type="text" placeholder="ID ASIGNACIÓN" value={updateAssignment.id} onChange={(e) => setUpdateAssignment({ ...updateAssignment, id: e.target.value })} />
          <select style={styles.select} value={updateAssignment.conductor} onChange={(e) => setUpdateAssignment({ ...updateAssignment, conductor: e.target.value })}>
            <option value="">Seleccione un conductor</option>
            {drivers.map((driver) => (
              <option key={driver} value={driver}>{driver}</option>
            ))}
          </select>
          <select style={styles.multiSelect} multiple value={updateAssignment.pasajeros} onChange={(e) => handlePassengerSelection(e, setUpdateAssignment, updateAssignment)}>
            <option value="">Seleccione pasajeros</option>
            {passengers.map((passenger) => (
              <option key={passenger} value={passenger}>{passenger}</option>
            ))}
          </select>
          <button style={styles.button} onClick={handleUpdateAssignment}>Actualizar asignación</button>
        </form>
      </div>

      <button style={styles.button} onClick={handleLogout}>Cerrar sesión</button>
    </div>
  );
};

export default GestionAsignaciones;
