import React, { useState } from 'react';
import './App.css'; // Asegúrate de crear este archivo CSS

const menuItems = [
  { name: 'Conductor' },
  { name: 'Pasajero' },
  { name: 'Rutas' },
  { name: 'Incidencias' },
  { name: 'Asignación' },
  { name: 'Auto' },
];

const conductores = [
  { rut: '17665368-9', nombre: 'PERSONA 1', numero: '986432156', estado: 'ACTIVO' },
  { rut: '17800268-9', nombre: 'PERSONA 1', numero: '985466970', estado: 'NO ACTIVO' },
  { rut: '17859268-9', nombre: 'PERSONA 1', numero: '984461863', estado: 'NO ACTIVO' },
];

const pasajeros = [
  { rut: '17665368-9', nombre: 'PERSONA 1', numero: '', estado: '' },
  { rut: '17800268-9', nombre: 'PERSONA 2', numero: '', estado: '' },
  { rut: '17859268-9', nombre: 'PERSONA 3', numero: '', estado: '' },
];

function App() {
  const [selectedMenu, setSelectedMenu] = useState('Conductor');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const renderTable = () => {
    const data = selectedMenu === 'Conductor' ? conductores : pasajeros;
    return (
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 text-left">RUT</th>
              <th className="px-4 py-2 text-left">NOMBRE</th>
              <th className="px-4 py-2 text-left">NÚMERO</th>
              <th className="px-4 py-2 text-left">ESTADO</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                <td className="px-4 py-2">{item.rut}</td>
                <td className="px-4 py-2">{item.nombre}</td>
                <td className="px-4 py-2">{item.numero}</td>
                <td className="px-4 py-2">{item.estado}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="app">
      <header className="header">
        <h1>PANEL DE CONTROL</h1>
        <button
          className="mobile-menu-button"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          Menu
        </button>
      </header>
      <div className="content">
        <nav className={`sidebar ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
          <div className="menu-items">
            {menuItems.map((item) => (
              <button
                key={item.name}
                className={`menu-item ${selectedMenu === item.name ? 'active' : ''}`}
                onClick={() => setSelectedMenu(item.name)}
              >
                <span>{item.name}</span>
              </button>
            ))}
          </div>
          <button className="logout-button">
            <span>CERRAR SESIÓN</span>
          </button>
        </nav>
        <main className="main-content ">{renderTable()}</main>
      </div>
    </div>
  );
}

export default App;
