import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import Logo from '../logo';
import '../../App.css'; // Para mover los estilos al archivo CSS

const BienvenidoConductor = () => {
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true); // Estado de carga

  const handleLogout = () => {
    setAuth(null);
    navigate('/'); // Redirige a la página de inicio
  };

  const handleNavigation = (path) => {
    navigate(path); // Redirige a la ruta correspondiente
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowLogo(false); // Oculta el logo después de 1 segundo
    }, 1000); // Tiempo para mostrar el logo

    const contentTimer = setTimeout(() => {
      setLoading(false); // Muestra el contenido después de 1 segundo
    }, 1000); // Simula carga de contenido

    return () => {
      clearTimeout(timer);
      clearTimeout(contentTimer);
    }; // Limpia el timer al desmontar
  }, []);

  return (
    <div className="panel-container">
     {showLogo && <Logo />} {/* Muestra el logo primero */}

      {!loading && ( // Muestra el contenido principal solo después de cargar
      
      <main className="main-content">
        <div className="control-panel">
        <h2 className="panel-title">BIENVENIDO CONDUCTOR</h2>
        <div className="management-options">
        <h3 className="options-title">¿QUÉ DESEAS HACER?</h3>
          
          <div className="button-container">
          <button
            type="button"
            className="control-button primer-button"
            onClick={() => handleNavigation('perfil/')}
          >
            Editar Perfil
          </button>
          <button
            type="button"
            className="control-button segundo-button "
            onClick={() => handleNavigation('ruta/')}
          >
            Ruta
          </button>
          <button
            type="button"
            className="control-button primer-button "
            onClick={() => handleNavigation('incidencias/')}
          >
            Incidencia
          </button>
          </div>
        </div>
        <button className="logout-button" onClick={handleLogout}>
          CERRAR SESIÓN
        </button>
      </div>
      </main>
      
      )}
    </div>
  );
};

export default BienvenidoConductor;
