import { Button } from "@/components/ui/button"
import React, { useContext } from 'react';
import { AuthContext } from '../context/authContext';
import '../App.css';
import Logo from './logo';

const PanelDeControl = () => {

    const { setAuth } = useContext(AuthContext);
  
  const handleLogout = () => {
    setAuth(null);
  };

  return (
    <div>
      <Logo/>



      {/* Main Content */}
      <main className="flex-grow flex flex-col items-center px-4 py-8">
        <div className="w-full max-w-md">
          <h2 className="text-xl font-semibold text-[#4a5d23] bg-[#4a5d23] text-white py-2 px-4 text-center mb-6">
            Panel de control
          </h2>

          <div className="bg-[#f9efe0] p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-medium text-center mb-4">
              ¿A QUÉ GESTIÓN DESEAS INGRESAR?
            </h3>

            <div className="space-y-4">
              <Button className="w-full bg-[#9b8250] hover:bg-[#7a6840] text-white">
                Conductor
              </Button>
              <Button className="w-full bg-[#d7a9a9] hover:bg-[#c89898] text-white">
                Pasajero
              </Button>
              <Button className="w-full bg-[#9b8250] hover:bg-[#7a6840] text-white">
                Rutas
              </Button>
              <Button className="w-full bg-[#d7a9a9] hover:bg-[#c89898] text-white">
                Incidencias
              </Button>
              <Button className="w-full bg-[#9b8250] hover:bg-[#7a6840] text-white">
                Asignación
              </Button>
            </div>
          </div>

          <Button className="w-full mt-6 bg-[#d1ccc7] hover:bg-[#bfbab5] text-black">
            CERRAR SESIÓN
          </Button>
        </div>
      </main>
    </div>
 );
};

export default PanelDeControl;
