import React, { useState, useContext } from 'react';
import { motion, AnimatePresence } from 'framer-motion'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/authContext';
import '../App.css'

const LoginForm = () => {
  const [credentials, setCredentials] = useState({ rut: '', password: '' });
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState(''); 

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/inicio', credentials);
      
      if (response.data && response.data.usuario) {
        setAuth(response.data);
        const role = response.data.usuario.role;

        if (role === 'admin') {
          navigate('/admin');
        } else if (role === 'conductor') {
          navigate('/conductor');
        } else if (role === 'pasajero') {
          navigate('/pasajero');
        } else {
          setErrorMessage('Rol desconocido'); 
        }
      } else {
        setErrorMessage('La respuesta no contiene los datos esperados'); 
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      setErrorMessage('Credenciales incorrectas. Inténtalo de nuevo.'); 
    }
  };

  return (
    <div class="login-box">
      <p>Iniciar Sesión</p>
    <form onSubmit={handleSubmit}>
      <div class="user-box">
      
        <input 
          type="text" 
          name=""
          placeholder="Ingresa tu RUT solo con guion" 
          value={credentials.rut} 
          onChange={handleChange} 
          required 
        />
        <label>Rut</label>
      </div>
      <div className="user-box">
      <label>Password</label>
        <input
          type="password"
          name=""
         
          
          placeholder="Ingresa tu contraseña"
          value={credentials.password}
          onChange={handleChange}
          required
        />
      </div>
      {errorMessage && <p className="text-danger">{errorMessage}</p>} 
      <a href='#'>
      <span></span>
      <span></span>
      <span></span>
      <span></span>
      Submit
      </a>
    </form>
    </div>
  );
};

export default LoginForm;
