import React, { useContext } from 'react';
import { AuthContext } from '../context/authContext';
import Logo from './logo';

const PanelDeControl = () => {
  const { setAuth } = useContext(AuthContext);
  
  const handleLogout = () => {
    setAuth(null);
  };

  // Estilos inline
  const styles = {
    panelContainer: {
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: '#f5e6d3',
    },
    mainContent: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: '2rem 1rem',
    },
    controlPanel: {
      width: '100%',
      maxWidth: '28rem',
    },
    panelTitle: {
      fontSize: '1.25rem',
      fontWeight: 600,
      color: 'white',
      backgroundColor: '#4a5d23',
      padding: '0.5rem 1rem',
      textAlign: 'center',
      marginBottom: '1.5rem',
    },
    managementOptions: {
      backgroundColor: '#f9efe0',
      padding: '1.5rem',
      borderRadius: '0.5rem',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    optionsTitle: {
      fontSize: '1.125rem',
      fontWeight: 500,
      textAlign: 'center',
      marginBottom: '1rem',
    },
    buttonContainer: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1rem',
    },
    button: {
      width: '100%',
      padding: '0.5rem',
      border: 'none',
      borderRadius: '0.25rem',
      cursor: 'pointer',
      fontWeight: 500,
      color: 'white',
    },
    conductorButton: {
      backgroundColor: '#9b8250',
    },
    pasajeroButton: {
      backgroundColor: '#d7a9a9',
    },
    logoutButton: {
      width: '100%',
      marginTop: '1.5rem',
      backgroundColor: '#d1ccc7',
      color: 'black',
      padding: '0.5rem',
      border: 'none',
      borderRadius: '0.25rem',
      cursor: 'pointer',
      fontWeight: 500,
    },
  };

  return (
    <div style={styles.panelContainer}>
      <Logo />
      <main style={styles.mainContent}>
        <div style={styles.controlPanel}>
          <h2 style={styles.panelTitle}>Panel de control</h2>

          <div style={styles.managementOptions}>
            <h3 style={styles.optionsTitle}>
              ¿A QUÉ GESTIÓN DESEAS INGRESAR?
            </h3>

            <div style={styles.buttonContainer}>
              <button style={{...styles.button, ...styles.conductorButton}}>Conductor</button>
              <button style={{...styles.button, ...styles.pasajeroButton}}>Pasajero</button>
              <button style={{...styles.button, ...styles.conductorButton}}>Rutas</button>
              <button style={{...styles.button, ...styles.pasajeroButton}}>Incidencias</button>
              <button style={{...styles.button, ...styles.conductorButton}}>Asignación</button>
            </div>
          </div>

          <button style={styles.logoutButton} onClick={handleLogout}>
            CERRAR SESIÓN
          </button>
        </div>
      </main>
    </div>
  );
};

export default PanelDeControl;
