import React, { useState } from 'react'
import { Button } from "@/components/ui/button"

export default function TransportPanel() {
  const [activeTab, setActiveTab] = useState('Asignación')

  const navItems = ['Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto']

  return (
    <div className="flex flex-col min-h-screen">
      <header className="bg-[url('/placeholder.svg?height=200&width=1920')] bg-cover bg-center py-8">
        <div className="container mx-auto text-center">
          <h1 className="text-4xl font-bold text-white">MK TRANSPORTE</h1>
        </div>
      </header>

      <nav className="bg-gray-100 shadow-md">
        <div className="container mx-auto flex items-center justify-between py-4">
          <div className="flex space-x-4">
            {navItems.map((item) => (
              <Button
                key={item}
                variant={activeTab === item ? "default" : "ghost"}
                onClick={() => setActiveTab(item)}
              >
                {item}
              </Button>
            ))}
          </div>
          <Button variant="destructive">CERRAR SESIÓN</Button>
        </div>
      </nav>

      <main className="flex-grow bg-white">
        <div className="container mx-auto mt-8">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-semibold mb-4">PANEL DE CONTROL - {activeTab}</h2>
            {/* Add content for each tab here */}
          </div>
        </div>
      </main>
    </div>
  )
}
