import React, { useState } from 'react';

const ControlPanel = () => {
  const [activeTab, setActiveTab] = useState('Conductor');

  const navItems = ['Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto'];

  const conductorData = [
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '986432156', estado: 'ACTIVO' },
    { rut: '17800268-9', nombre: 'PERSONA 1', numero: '985466970', estado: 'NO ACTIVO' },
    { rut: '17859268-9', nombre: 'PERSONA 1', numero: '984461863', estado: 'NO ACTIVO' },
  ];

  const styles = {
    container: {
      display: 'flex',
      height: '100vh',
      fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
      width: '200px',
      backgroundColor: '#f0f0f0',
      padding: '20px',
      display: 'flex',
      flexDirection: 'column' ,
    },
    navItem: {
      padding: '10px',
      marginBottom: '5px',
      cursor: 'pointer',
      borderRadius: '5px',
    },
    activeNavItem: {
      backgroundColor: '#4a90e2',
      color: 'white',
    },
    closeSession: {
      marginTop: 'auto',
      padding: '10px',
      backgroundColor: '#e74c3c',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
    content: {
      flex: 1,
      padding: '20px',
    },
    header: {
      backgroundColor: '#2c3e50',
      color: 'white',
      padding: '20px',
      marginBottom: '20px',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
    },
    th: {
      backgroundColor: '#f2f2f2',
      padding: '10px',
      textAlign: 'left' ,
      borderBottom: '1px solid #ddd',
    },
    td: {
      padding: '10px',
      borderBottom: '1px solid #ddd',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        {navItems.map((item) => (
          <div
            key={item}
            style={{
              ...styles.navItem,
              ...(activeTab === item ? styles.activeNavItem : {}),
            }}
            onClick={() => setActiveTab(item)}
          >
            {item}
          </div>
        ))}
        <button style={styles.closeSession}>CERRAR SESIÓN</button>
      </div>
      <div style={styles.content}>
        <div style={styles.header}>
          <h1>PANEL DE CONTROL</h1>
        </div>
        <h2>{activeTab}</h2>
        {activeTab === 'Conductor' && (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>RUT</th>
                <th style={styles.th}>NOMBRE</th>
                <th style={styles.th}>NÚMERO</th>
                <th style={styles.th}>ESTADO</th>
              </tr>
            </thead>
            <tbody>
              {conductorData.map((conductor) => (
                <tr key={conductor.rut}>
                  <td style={styles.td}>{conductor.rut}</td>
                  <td style={styles.td}>{conductor.nombre}</td>
                  <td style={styles.td}>{conductor.numero}</td>
                  <td style={styles.td}>{conductor.estado}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ControlPanel;
