import React, { useState, useEffect } from 'react';
import '../App.css';
import logo from '../images/paisaje.jpg'; // Asegúrate de que la ruta sea correcta

const App = () => {
    const [isRevealed, setIsRevealed] = useState(false);
    const [imageLoaded, setImageLoaded] = useState(false);
  
    const handleClick = () => {
      setIsRevealed(true);
    };
  
    useEffect(() => {
      const img = new Image();
      img.onload = () => {
        setImageLoaded(true);
      };
      img.src = logo;
    }, []);
  
    return (
      <div className={`app-container ${isRevealed ? 'revealed' : ''}`}>
        <div className={`logo-container ${isRevealed ? 'move-up' : ''}`} onClick={handleClick}>
          {imageLoaded ? (
            <img src={logo} alt="Logo" className="logo" />
          ) : (
            <div className="logo-placeholder">Cargando...</div>
          )}
        </div>
        <div className={`login-container ${isRevealed ? 'show' : ''}`}>
          <h1>Inicio de sesión</h1>
          <form>
            <div className="form-group">
              <label htmlFor="email">Correo electrónico:</label>
              <input type="email" id="email" name="email" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Contraseña:</label>
              <input type="password" id="password" name="password" />
            </div>
            <button type="submit">Iniciar sesión</button>
          </form>
        </div>
      </div>
    );
  };
  
  export default App;
