import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [conductores, setConductores] = useState([]);
  const [pasajeros, setPasajeros] = useState([]);
  const [nuevoConductor, setNuevoConductor] = useState({ rut: '', nombre: '', apellido: '', password: '', telefono: '' });
  const [nuevoPasajero, setNuevoPasajero] = useState({ rut: '', nombre: '', apellido: '', password: '', telefono: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No hay token de autenticación. Por favor, inicie sesión.');
        setLoading(false);
        return;
      }
      try {
        const [conductoresRes, pasajerosRes] = await Promise.all([
          axios.get('http://localhost:3000/api/admin/conductores'),
          axios.get('http://localhost:3000/api/admin/pasajeros')
        ]);
        setConductores(conductoresRes.data);
        setPasajeros(pasajerosRes.data);
        setError(null);
      } catch (err) {
        console.error('Error completo:', err);
        if (err.response) {
          console.error('Datos de la respuesta:', err.response.data);
          console.error('Status:', err.response.status);
          console.error('Headers:', err.response.headers);
        }
        setError('Error al cargar los datos: ' + (err.response?.data?.message || err.message));
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCrearConductor = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/admin/conductores', nuevoConductor);
      setConductores([...conductores, response.data]);
      setNuevoConductor({ rut: '', nombre: '', apellido: '', password: '', telefono: '' });
    } catch (error) {
      console.error('Error al crear conductor:', error);
      setError('Error al crear conductor: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleCrearPasajero = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/admin/pasajeros', nuevoPasajero);
      setPasajeros([...pasajeros, response.data]);
      setNuevoPasajero({ rut: '', nombre: '', apellido: '', password: '', telefono: '' });
    } catch (error) {
      console.error('Error al crear pasajero:', error);
      setError('Error al crear pasajero: ' + (error.response?.data?.message || error.message));
    }
  };

  if (loading) return <div>Cargando...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Dashboard del Administrador</h1>
      
      {/* Formulario para crear conductores */}
      <h2>Crear Conductor</h2>
      <form onSubmit={handleCrearConductor}>
        {/* ... (inputs para nuevoConductor) */}
        <button type="submit">Crear Conductor</button>
      </form>

      {/* Lista de conductores */}
      <h2>Conductores</h2>
      <ul>
        {conductores.map(conductor => (
          <li key={conductor._id}>{conductor.nombre} {conductor.apellido}</li>
        ))}
      </ul>
      {/* Formulario para crear pasajeros */}
      <h2>Crear Pasajero</h2>
      <form onSubmit={handleCrearPasajero}>
        <input type="text" placeholder="RUT" value={nuevoPasajero.rut} onChange={(e) => setNuevoPasajero({ ...nuevoPasajero, rut: e.target.value })} required />
        <input type="text" placeholder="Nombre" value={nuevoPasajero.nombre} onChange={(e) => setNuevoPasajero({ ...nuevoPasajero, nombre: e.target.value })} required />
        <input type="text" placeholder="Apellido" value={nuevoPasajero.apellido} onChange={(e) => setNuevoPasajero({ ...nuevoPasajero, apellido: e.target.value })} required />
        <input type="password" placeholder="Contraseña" value={nuevoPasajero.password} onChange={(e) => setNuevoPasajero({ ...nuevoPasajero, password: e.target.value })} required />
        <input type="text" placeholder="Teléfono" value={nuevoPasajero.telefono} onChange={(e) => setNuevoPasajero({ ...nuevoPasajero, telefono: e.target.value })} />
        <button type="submit">Crear Pasajero</button>
      </form>

      {/* Lista de pasajeros */}
      <h2>Pasajeros</h2>
      <ul>
        {pasajeros.map(pasajero => (
          <li key={pasajero._id}>{pasajero.nombre} {pasajero.apellido}</li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
