import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/authContext';

const LoginForm = () => {
  const [credentials, setCredentials] = useState({ rut: '', password: '' });
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/inicio', credentials);
      if (response.data && response.data.usuario) {
        setAuth(response.data);
        const role = response.data.usuario.role;
        if (role === 'admin') {
          navigate('/admin');
        } else if (role === 'conductor') {
          navigate('/conductor');
        } else if (role === 'pasajero') {
          navigate('/pasajero');
        }
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="px-5 py-3">
      <div className="form-group mb-3">
        <label className="text-login1" htmlFor="rut">RUT</label>
        <input type="text" name="rut" className="form-control" id="rut" placeholder="Ingresa tu RUT solo con guion" value={credentials.rut} onChange={handleChange} required/>
      </div>
      <div className="form-group mb-4">
        <label className="text-login1" htmlFor="password">Contraseña</label>
        <input
          type="password"
          name="password"
          className="form-control"
          id="password"
          placeholder="Ingresa tu contraseña"
          value={credentials.password}
          onChange={handleChange}
          required
        />
      </div>
      <div className="d-flex justify-content-center">
        <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
      </div>
    </form>
  );
};

export default LoginForm;
