import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import Logo from '../logo';

const VerAsignaciones = () => {
  const { setAuth } = useContext(AuthContext);
  const [asignacion, setAsignacion] = useState({
    idRuta: 'R105',
    idAsignacion: 'A105',
    rutConductor: '16849724-7',
    nombreConductor: 'PERSONA 1',
    numero: '962147595',
  });

  const handleVolverAlPanel = () => {
    // Aquí iría la lógica para volver al panel principal
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
    },
    title: {
      backgroundColor: '#4a5d23',
      color: 'white',
      padding: '10px',
      textAlign: 'center',
      marginBottom: '20px',
    },
    asignacionInfo: {
      backgroundColor: '#ffe4b5',
      padding: '20px',
      borderRadius: '5px',
      marginBottom: '20px',
    },
    label: {
      fontWeight: 'bold',
      marginRight: '10px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#9b8250',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '16px',
    },
  };

  return (
    <div>
      < Logo/>
    <div style={styles.container}>
      
      <h1 style={styles.title}>VER ASIGNACIONES</h1>
      <div style={styles.asignacionInfo}>
        <h2>CONDUCTOR DESIGNADO</h2>
        <p><span style={styles.label}>ID RUTA:</span>{asignacion.idRuta}</p>
        <p><span style={styles.label}>ID ASIGNACIÓN:</span>{asignacion.idAsignacion}</p>
        <p><span style={styles.label}>RUT CONDUCTOR:</span>{asignacion.rutConductor}</p>
        <p><span style={styles.label}>NOMBRE CONDUCTOR:</span>{asignacion.nombreConductor}</p>
        <p><span style={styles.label}>NÚMERO:</span>{asignacion.numero}</p>
      </div>
      <button style={styles.button} onClick={handleVolverAlPanel}>
        VOLVER AL PANEL
      </button>
    </div>
    </div>
  );
};

export default VerAsignaciones;
