import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../context/authContext';

// Datos simulados
const mockPassengers = [
  '19234567-8', '18345678-9', '17456789-0', '16567890-1', '15678901-2', 
  '14789012-3', '13890123-4', '12901234-5', '11012345-6', '10123456-7'
];

const mockDrivers = ['17665368-9', '17800268-9', '17859268-9'];

const MAX_PASSENGERS = 5; // Número máximo de pasajeros permitidos

const GestionAsignaciones = () => {
  const { setAuth } = useContext(AuthContext);
  
  const [newAssignment, setNewAssignment] = useState({
    id: `A${Math.floor(Math.random() * 900) + 100}`,
    conductor: '',
    pasajeros: Array(MAX_PASSENGERS).fill('') // Inicializa array con strings vacíos
  });

  const [updateAssignment, setUpdateAssignment] = useState({
    id: '',
    conductor: '',
    pasajeros: Array(MAX_PASSENGERS).fill('')
  });

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
    },
    section: {
      backgroundColor: '#e6f3ff',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
      marginBottom: '20px',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '10px',
    },
    passengerSection: {
      border: '1px solid #ccc',
      padding: '15px',
      borderRadius: '5px',
      backgroundColor: '#f5f5f5',
      marginBottom: '10px',
    },
    select: {
      width: '100%',
      padding: '8px',
      marginBottom: '10px',
      borderRadius: '4px',
      border: '1px solid #ccc',
    },
    button: {
      padding: '10px',
      backgroundColor: '#4a5d23',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
    },
    label: {
      marginBottom: '5px',
      fontWeight: 'bold',
    }
  };

  const handleNewPassengerChange = (index, value) => {
    const newPassengers = [...newAssignment.pasajeros];
    newPassengers[index] = value;
    setNewAssignment({
      ...newAssignment,
      pasajeros: newPassengers
    });
  };

  const handleUpdatePassengerChange = (index, value) => {
    const newPassengers = [...updateAssignment.pasajeros];
    newPassengers[index] = value;
    setUpdateAssignment({
      ...updateAssignment,
      pasajeros: newPassengers
    });
  };

  const handleSubmitNew = (e) => {
    e.preventDefault();
    const filteredPassengers = newAssignment.pasajeros.filter(p => p !== '');
    console.log('Nueva asignación:', {
      ...newAssignment,
      pasajeros: filteredPassengers
    });
  };

  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const filteredPassengers = updateAssignment.pasajeros.filter(p => p !== '');
    console.log('Actualizar asignación:', {
      ...updateAssignment,
      pasajeros: filteredPassengers
    });
  };

  return (
    <div style={styles. container}>
      <h2>Crear Asignación</h2>
      <section style={styles.section}>
        <form style={styles.form} onSubmit={handleSubmitNew}>
          <label style={styles.label}>Conductor:</label>
          <select style={styles.select} value={newAssignment.conductor} onChange={(e) => setNewAssignment({ ...newAssignment, conductor: e.target.value })}>
            <option value="">Seleccione un conductor</option>
            {mockDrivers.map((driver) => (
              <option key={driver} value={driver}>{driver}</option>
            ))}
          </select>
          <div style={styles.passengerSection}>
            <label style={styles.label}>Pasajeros:</label>
            {Array(MAX_PASSENGERS).fill(0).map((_, index) => (
              <select key={index} style={styles.select} value={newAssignment.pasajeros[index]} onChange={(e) => handleNewPassengerChange(index, e.target.value)}>
                <option value="">Seleccione un pasajero</option>
                {mockPassengers.map((passenger) => (
                  <option key={passenger} value={passenger}>{passenger}</option>
                ))}
              </select>
            ))}
          </div>
          <button style={styles.button} type="submit">Crear Asignación</button>
        </form>
      </section>

      <h2>Actualizar Asignación</h2>
      <section style={styles.section}>
        <form style={styles.form} onSubmit={handleSubmitUpdate}>
          <label style={styles.label}>ID de la asignación:</label>
          <input type="text" value={updateAssignment.id} onChange={(e) => setUpdateAssignment({ ...updateAssignment, id: e.target.value })} />
          <label style={styles.label}>Conductor:</label>
          <select style={styles.select} value={updateAssignment.conductor} onChange={(e) => setUpdateAssignment({ ...updateAssignment, conductor: e.target.value })}>
            <option value="">Seleccione un conductor</option>
            {mockDrivers.map((driver) => (
              <option key={driver} value={driver}>{driver}</option>
            ))}
          </select>
          <div style={styles.passengerSection}>
            <label style={styles.label}>Pasajeros:</label>
            {Array(MAX_PASSENGERS).fill(0).map((_, index) => (
              <select key={index} style={styles.select} value={updateAssignment.pasajeros[index]} onChange={(e) => handleUpdatePassengerChange(index, e.target.value)}>
                <option value="">Seleccione un pasajero</option>
                {mockPassengers.map((passenger) => (
                  <option key={passenger} value={passenger}>{passenger}</option>
                ))}
              </select>
            ))}
          </div>
          <button style={styles.button} type="submit">Actualizar Asignación</button>
        </form>
      </section>
    </div>
  );
};

export default GestionAsignaciones;
