import { useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import '../App.css'
import logoImage from '../images/titulo.png';

export default function FullScreenLanding() {
    const [showLogin, setShowLogin] = useState(false)
  
    const handleImageClick = () => {
      setShowLogin(true)
    }
  
    return (
      <div className="full-screen-container">
        <AnimatePresence>
          {!showLogin && (
            <>
              <motion.div
                initial={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 0.8 }}
                className="background-container"
                onClick={handleImageClick}
              />
              <motion.div
                initial={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 0.8 }}
                className="logo-container"
              >
                <img 
                  src={logoImage} 
                  alt="Logo" 
                  className="logo" 
                  onClick={handleImageClick}
                />
              </motion.div>
            </>
          )}
        </AnimatePresence>
  
        <AnimatePresence>
          {showLogin && (
            <motion.div
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              className="login-container"
            >
            <div id="form-ui">
                <form action="" method="post" id="form">
                <div id="form-body">
                    <div id="welcome-lines">
                    <div id="welcome-line-1">Inicia Sesión</div>
                    <div id="welcome-line-2">¡Bienvenido!</div>
                    </div>
                    <div id="input-area">
                    <div class="form-inp">
                        <input placeholder="Rut" type="text"/>
                    </div>
                    <div class="form-inp">
                        <input placeholder="Contraeña" type="password"/>
                    </div>
                    </div>
                    <div id="submit-button-cvr">
                    <button id="submit-button" type="submit">Login</button>
                    </div>
                    
                </div>
                </form>
                </div>


            </motion.div>
          )}
        </AnimatePresence>
      </div>
    )
  }
