import React, { useState } from 'react';
import Logo from './logo';
import '../App.css'

export default function ControlPanel() {
  const [activeTab, setActiveTab] = useState('Incidencias');

  const navItems = ['Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto'];

  return (
    <div className="control-panel">
      <header className="header">
        <div className="logo-container">
          <Logo />
        </div>
        <nav className="nav">
          {navItems.map((item) => (
            <button
              key={item}
              className={`nav-button ${activeTab === item ? 'active' : ''}`}
              onClick={() => setActiveTab(item)}
            >
              {item}
            </button>
          ))}
          <button className="logout-button">CERRAR SESION</button>
        </nav>
      </header>

      <main className="main-content">
        <div className="content-box">
          <h1 className="content-title">PANEL DE CONTROL - {activeTab}</h1>
          
          {/* Contenido específico de la pestaña activa */}
          {activeTab === 'Incidencias' && (
            <div>
              <p>Contenido de Incidencias</p>
              {/* Añade aquí el contenido específico de Incidencias */}
            </div>
          )}
          {/* Añade condiciones similares para otras pestañas */}
        </div>
      </main>
    </div>
  );
}
