import React, { useState } from 'react';
import '../../App.css'
import Logo from '../logo';
import Perfil from '../pasajero/perfilPasajero'

const ControlPanel = () => {
    const [activeTab, setActiveTab] = useState('Conductor');
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  
    const navItems = ['Perfil','Asignacion'];
  
    const renderContent = () => {
      switch(activeTab) {
         case 'Perfil':
           return <Perfil />;
        // case 'Conductor':
        //   return <Conductor />;
        // case 'Pasajero':
        //   return <Pasajero />;
        // case 'Rutas':
        // return <Rutas />;
        // case 'Incidencias':
        //   return <Incidencias />;
        // case 'Asignación':
        //   return <Asignacion />;
        // case 'Vehiculo':
        //   return <Vehiculo />;
        default:
          return <div>Contenido no encontrado</div>;
      }
    };
  
    return (
      <div>
          <Logo/>
     
          <div className="control-panel-container"> <button
                className="menu-toggle"
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              >
                &#9776;
              </button>
              <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
                {navItems.map((item) => (
                  <div
                    key={item}
                    className={`nav-item ${activeTab === item ? 'active' : ''}`}
                    onClick={() => setActiveTab(item)}
                  >
                    {item}
                  </div>
                ))}
                <button className="close-session">Cerrar Sesión</button>

              </div>
              <div className="content">
                <div className="header">
                  <h1>PANEL DE CONTROL</h1>
                </div>
                {renderContent()}
              </div>
          </div>
      </div>
  );
};
  
  export default ControlPanel;
