import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/authContext';

const LoginForm = () => {
  const [credentials, setCredentials] = useState({ rut: '', password: '' });
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();
  
  // Nuevo estado para controlar la visibilidad del mensaje de error
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
    setError(''); // Limpiar error cuando se cambia el input
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/inicio', credentials);
      
      if (response.data && response.data.usuario) {
        setAuth(response.data);
        const role = response.data.usuario.role;

        if (role === 'admin') {
          navigate('/admin');
        } else if (role === 'conductor') {
          navigate('/conductor');
        } else if (role === 'pasajero') {
          navigate('/pasajero');
        }
      } else {
        setError('La respuesta no contiene los datos esperados.');
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      setError('Credenciales incorrectas. Inténtalo de nuevo.'); // Establecer el mensaje de error
    }
  };

  return (
    <div>
      {/* Mensaje de error usando Bootstrap */}
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="px-5 py-3">
        <div className="form-group mb-3">
          <label className="text-login1" htmlFor="rut">RUT</label>
          <input 
            type="text" 
            name="rut" 
            className="form-control" 
            id="rut" 
            placeholder="Ingresa tu RUT solo con guion" 
            value={credentials.rut} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group mb-4">
          <label className="text-login1" htmlFor="password">Contraseña</label>
          <input
            type="password"
            name="password"
            className="form-control"
            id="password"
            placeholder="Ingresa tu contraseña"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="d-flex justify-content-center">
          <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
