import React, { useState } from 'react';

export default function ControlPanel() {
  const [activeTab, setActiveTab] = useState('Conductor');

  const sidebarItems = ['Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto'];

  const drivers = [
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '984632256', estado: 'ACTIVO' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '985468970', estado: 'NO ACTIVO' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '984461863', estado: 'NO ACTIVO' },
  ];

  const passengers = [
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '', estado: '' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '', estado: '' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '', estado: '' },
  ];

  return (
    <div>
      {/* Top Container */}
      <div style={{
        backgroundImage: 'url("../src/images/fondo.png")', // Reemplaza con la ruta de tu imagen de fondo
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '200px', // Ajusta la altura según tus necesidades
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}>
        {/* Logo */}
        <img src="/path-to-your-logo.png" alt="Logo" style={{ maxHeight: '80%' }} /> {/* Reemplaza con la ruta de tu logo */}
      </div>

      {/* Main Content */}
      <div style={{ padding: '20px' }}>
        <div style={{ display: 'flex', backgroundColor: 'white', borderRadius: '10px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' }}>
          {/* Sidebar */}
          <div style={{ width: '200px', backgroundColor: '#f0f0f0', padding: '1rem', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
            <div>
              <h2 style={{ fontSize: '1.2rem', fontWeight: 'bold', marginBottom: '1rem' }}>¿A QUÉ GESTIÓN DESEAS INGRESAR?</h2>
              {sidebarItems.map((item) => (
                <button
                  key={item}
                  style={{
                    width: '100%',
                    padding: '0.5rem',
                    marginBottom: '0.5rem',
                    backgroundColor: activeTab === item ? '#007bff' : 'transparent',
                    color: activeTab === item ? 'white' : 'black',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                  onClick={() => setActiveTab(item)}
                >
                  {item}
                </button>
              ))}
            </div>
            <button style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '0.5rem' }}>CERRAR SESION</button>
          </div>

          {/* Content Area */}
          <div style={{ flex: 1, padding: '1.5rem' }}>
            <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '1rem' }}>PANEL DE CONTROL</h1>
            
            { /* Aquí va el contenido según la pestaña activa */ }
          </div>
        </div>
      </div>
    </div>
  );
}
