import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import '../../App.css';

const VerAsignaciones = () => {
  const { setAuth } = useContext(AuthContext);
  const [asignacion, setAsignacion] = useState({
    idRuta: 'R105',
    idAsignacion: 'A105',
    rutConductor: '16849724-7',
    nombreConductor: 'PERSONA 1',
    numero: '962147595',
  });

  const handleVolverAlPanel = () => {
    // Implementar lógica para volver al panel
  };

  return (
    <div className='profile-container'>
      <h2 className='profile-title'>Ver Asignaciones</h2>
      <div className='profile-content'>
        <div className='profile-info'>
          <h3>Conductor Designado</h3>
          <p><strong>ID Ruta:</strong> {asignacion.idRuta}</p>
          <p><strong>ID Asignación:</strong> {asignacion.idAsignacion}</p>
          <p><strong>RUT Conductor:</strong> {asignacion.rutConductor}</p>
          <p><strong>Nombre Conductor:</strong> {asignacion.nombreConductor}</p>
          <p><strong>Número:</strong> {asignacion.numero}</p>
        </div>
        <button className='profile-button' onClick={handleVolverAlPanel}>Volver al Panel</button>
      </div>
    </div>
  );
};

export default VerAsignaciones;
