import React, { useContext } from 'react';
import { Navigate } from 'react-router-dom'; // Descomentar
import { AuthContext } from '../context/authContext';

const PrivateRoute = ({ children, role }) => {
  const { auth } = useContext(AuthContext);

  if (!auth) {
    // Si no está autenticado, redirigir al login
    return <Navigate to="/" />;
  }

  if (role && auth.role !== role) {
    // Si el rol no coincide, redirigir a la página de acceso denegado o bienvenida
    return <Navigate to="/" />;
  }

  // Si está autenticado y tiene el rol adecuado, renderiza los children
  return children;
};

export default PrivateRoute;
