import React, { useState, useEffect, useContext, useCallback } from 'react';
import { AuthContext } from '../context/authContext';
import axios from 'axios';

const AdminDashboard = () => {
  const { auth, setAuth } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUsers = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/admin/users', {
        headers: { Authorization: `Bearer ${auth?.token}` }
      });
      setUsers(response.data);
      setLoading(false);
    } catch (err) {
      setError('Error al cargar los usuarios');
      setLoading(false);
    }
  }, [auth?.token]);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const handleLogout = () => {
    setAuth(null);
    // Aquí puedes agregar cualquier otra lógica de limpieza necesaria
  };

  if (loading) return <div style={styles.container}>Cargando...</div>;
  if (error) return <div style={styles.container}>{error}</div>;

  return (
    <div style={styles.container}>
      <h1>Dashboard de Administrador</h1>
      <p>Bienvenido, {auth?.email}</p>
      <button onClick={handleLogout} style={styles.button}>Cerrar Sesión</button>

      <h2>Lista de Usuarios</h2>
      <ul style={styles.userList}>
        {users.map(user => (
          <li key={user.id} style={styles.userItem}>
            {user.name} - {user.email} - {user.role}
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    marginTop: '50px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    marginBottom: '20px',
  },
  userList: {
    listStyleType: 'none',
    padding: 0,
    maxWidth: '600px',
    margin: '0 auto',
  },
  userItem: {
    padding: '10px',
    borderBottom: '1px solid #ccc',
    textAlign: 'left',
  },
};

export default AdminDashboard;
