import React, { useContext } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { AuthContext } from '../context/authContext';

const PrivateRoute = ({ allowedRoles }) => {
  const { auth } = useContext(AuthContext);

  if (!auth) {
    // Si no hay usuario autenticado, redirigir al inicio
    return <Navigate to="/" replace />;
  }

  if (allowedRoles && !allowedRoles.includes(auth.usuario.role)) {
    // Si el rol del usuario no est치 en los roles permitidos, redirigir al inicio
    return <Navigate to="/" replace />;
  }

  // Si el usuario est치 autenticado y tiene el rol correcto, renderizar los componentes hijos
  return <Outlet />;
};

export default PrivateRoute;
