import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';


const EditarPerfilPasajero = () => {
  const { setAuth } = useContext(AuthContext);
  const [perfil, setPerfil] = useState({
    rut: '15452684-9',
    nombre: 'PERSONA1',
    numero: '974859214',
    direccion: 'AV 123',
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setPerfil(prevPerfil => ({
      ...prevPerfil,
      [name]: value,
    }));
  };

  const handleGuardarCambios = () => {
    // Aquí iría la lógica para guardar los cambios
    console.log('Perfil actualizado:', perfil);
  };

  const handleVolverAlPanel = () => {
    // Aquí iría la lógica para volver al panel principal
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
    },
    title: {
      backgroundColor: '#4a5d23',
      color: 'white',
      padding: '10px',
      textAlign: 'center' ,
      marginBottom: '20px',
    },
    form: {
      backgroundColor: '#ffe4b5',
      padding: '20px',
      borderRadius: '5px',
      marginBottom: '20px',
    },
    label: {
      display: 'block',
      marginBottom: '5px',
    },
    input: {
      width: '100%',
      padding: '8px',
      marginBottom: '15px',
      borderRadius: '3px',
      border: '1px solid #ccc',
    },
    button: {
      width: '100%',
      padding: '10px',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '16px',
      marginBottom: '10px',
    },
    guardarButton: {
      backgroundColor: '#4a5d23',
      color: 'white',
    },
    volverButton: {
      backgroundColor: '#9b8250',
      color: 'white',
    },
  };

  return (
   
    <div style={styles.container}>
      
      <h1 style={styles.title}>EDITAR MI PERFIL</h1>
      <div style={styles.form}>
        <label style={styles.label}>RUT</label>
        <input
          style={styles.input}
          type="text"
          name="rut"
          value={perfil.rut}
          onChange={handleInputChange}
          readOnly
        />
        <label style={styles.label}>NOMBRE</label>
        <input
          style={styles.input}
          type="text"
          name="nombre"
          value={perfil.nombre}
          onChange={handleInputChange}
        />
        <label style={styles.label}>NÚMERO</label>
        <input
          style={styles.input}
          type="text"
          name="numero"
          value={perfil.numero}
          onChange={handleInputChange}
        />
        <label style={styles.label}>DIRECCIÓN</label>
        <input
          style={styles.input}
          type="text"
          name="direccion"
          value={perfil.direccion}
          onChange={handleInputChange}
        />
        <button style={{...styles.button, ...styles.guardarButton}} onClick={handleGuardarCambios}>
          Guardar Cambios
        </button>
      </div>
      <button style={{...styles.button, ...styles.volverButton}} onClick={handleVolverAlPanel}>
       
      </button>
    </div>
    
  );
};

export default EditarPerfilPasajero;
