import React, { useContext } from 'react';
import { AuthContext } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import Logo from '../logo';
import '../../App.css'; // Para mover los estilos al archivo CSS

const BienvenidoConductor = () => {
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true); // Estado de carga

  const handleLogout = () => {
    setAuth(null);
    navigate('/'); // Redirige a la página de inicio
  };

  const handleNavigation = (path) => {
    navigate(path); // Redirige a la ruta correspondiente
  };

  useEffect(() => {
    // Simula una carga
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000); // Espera 1 segundo

    return () => clearTimeout(timer); // Limpia el timer al desmontar
  }, []);

  return (
    <div className="panel-container">
      {loading ? ( // Muestra un spinner o un placeholder mientras carga
        <div className="loading-container">
          <p>Cargando...</p> {/* Puedes cambiar esto por un spinner */}
        </div>
      ) : (
        <>
          <Logo />
      <main className="main-content">
        <div className="control-panel">
        <h2 className="panel-title">BIENVENIDO CONDUCTOR</h2>
        <div className="management-options">
        <h3 className="options-title">¿QUÉ DESEAS HACER?</h3>
          
          <div className="button-container">
          <button
            type="button"
            className="control-button primer-button"
            onClick={() => handleNavigation('perfil/')}
          >
            Editar Perfil
          </button>
          <button
            type="button"
            className="control-button segundo-button "
            onClick={() => handleNavigation('ruta/')}
          >
            Ruta
          </button>
          <button
            type="button"
            className="control-button primer-button "
            onClick={() => handleNavigation('incidencias/')}
          >
            Incidencia
          </button>
          </div>
        </div>
        <button className="logout-button" onClick={handleLogout}>
          CERRAR SESIÓN
        </button>
      </div>
      </main>
      </>
      )}
    </div>
  );
};

export default BienvenidoConductor;
