// GestionIncidenciasPasajero.jsx
import React, { useState } from 'react';

import '../../App.css';

const GestionIncidenciasPasajero = () => {
  const [incidencias, setIncidencias] = useState([
    { id: 'I105', detalles: 'Detalles de la incidencia I105', estado: 'Pendiente', fecha: '2023-07-01' },
    { id: 'I106', detalles: 'Detalles de la incidencia I106', estado: 'En Proceso', fecha: '2023-07-02' },
    { id: 'I107', detalles: 'Detalles de la incidencia I107', estado: 'Resuelto', fecha: '2023-07-03' },
  ]);

  const [incidenciaSeleccionada, setIncidenciaSeleccionada] = useState(null);
  const [nuevaIncidencia, setNuevaIncidencia] = useState({ idAsignacion: '', descripcion: '', fecha: '' });

  const handleVerDetalles = (incidencia) => {
    setIncidenciaSeleccionada(incidencia);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNuevaIncidencia(prev => ({ ...prev, [name]: value }));
  };

  const handleCrearIncidencia = (e) => {
    e.preventDefault();
    const nuevaIncidenciaCompleta = { 
      ...nuevaIncidencia, 
      id: `I${Math.floor(Math.random() * 1000)}`,
      estado: 'Pendiente'
    };
    setIncidencias([...incidencias, nuevaIncidenciaCompleta]);
    setNuevaIncidencia({ idAsignacion: '', descripcion: '', fecha: '' });
  };

  return (
    <div className='user-container'>
      
      <h1 className='user-title'>Gestión de Incidencias - Pasajero</h1>
      
      <section className='user-section'>
        <h2 className='user-subtitle'>Mis Incidencias</h2>
        <table className='user-table'>
          <thead>
            <tr>
              <th className='user-th'>ID</th>
              <th className='user-th'>Estado</th>
              <th className='user-th'>Fecha</th>
              <th className='user-th'>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {incidencias.map((incidencia) => (
              <tr key={incidencia.id}>
                <td className='user-td'>{incidencia.id}</td>
                <td className='user-td'>{incidencia.estado}</td>
                <td className='user-td'>{incidencia.fecha}</td>
                <td className='user-td'>
                  <button className='user-button' onClick={() => handleVerDetalles(incidencia)}>Ver Detalles</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {incidenciaSeleccionada && (
          <div>
            <h3>Detalles de la Incidencia</h3>
            <p>{incidenciaSeleccionada.detalles}</p>
          </div>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-subtitle'>Reportar Nueva Incidencia</h2>
        <form className='user-form' onSubmit={handleCrearIncidencia}>
          <input 
            className='user-input' 
            type="text" 
            name="idAsignacion"
            placeholder="ID ASIGNACIÓN" 
            value={nuevaIncidencia.idAsignacion}
            onChange={handleInputChange}
          />
          <textarea 
            className='user-input' 
            name="descripcion"
            placeholder="DESCRIPCIÓN"
            value={nuevaIncidencia.descripcion}
            onChange={handleInputChange}
          ></textarea>
          <input 
            className='user-input' 
            type="date" 
            name="fecha"
            value={nuevaIncidencia.fecha}
            onChange={handleInputChange}
          />
          <button className='user-button' type="submit">Reportar Incidencia</button>
        </form>
      </section>

      <button className='user-button' onClick={() => {/* Lógica para volver al panel principal */}}>
        VOLVER AL PANEL
      </button>
    </div>
  );
};

export default GestionIncidenciasPasajero;
