import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Asumiendo que usar치s axios para las peticiones HTTP

const GestionConductor = () => {
  const [conductores, setConductores] = useState([]);
  const [nuevoCondcutor, setNuevoConductor] = useState({
    rut: '',
    nombre: '',
    rol: '',
    numero: '',
    contrase침a: ''
  });
  const [conductorActualizar, setConductorActualizar] = useState({
    rut: '',
    nombre: '',
    numero: ''
  });
  const [conductorEliminar, setConductorEliminar] = useState({
    rut: '',
    datos: ''
  });
  const [conductorEstado, setConductorEstado] = useState({
    rut: '',
    estado: ''
  });

  useEffect(() => {
    // Cargar conductores al montar el componente
    cargarConductores();
  }, []);

  const cargarConductores = async () => {
    try {
      const response = await axios.get('/api/conductores');
      setConductores(response.data);
    } catch (error) {
      console.error('Error al cargar conductores:', error);
    }
  };

  const agregarConductor = async () => {
    try {
      await axios.post('/api/conductores', nuevoCondcutor);
      setNuevoConductor({ rut: '', nombre: '', rol: '', numero: '', contrase침a: '' });
      cargarConductores(); // Recargar la lista de conductores
    } catch (error) {
      console.error('Error al agregar conductor:', error);
    }
  };

  const actualizarConductor = async () => {
    try {
      await axios.put(`/api/conductores/${conductorActualizar.rut}`, conductorActualizar);
      setConductorActualizar({ rut: '', nombre: '', numero: '' });
      cargarConductores();
    } catch (error) {
      console.error('Error al actualizar conductor:', error);
    }
  };

  const eliminarConductor = async () => {
    try {
      await axios.delete(`/api/conductores/${conductorEliminar.rut}`);
      setConductorEliminar({ rut: '', datos: '' });
      cargarConductores();
    } catch (error) {
      console.error('Error al eliminar conductor:', error);
    }
  };

  const actualizarEstadoConductor = async () => {
    try {
      await axios.patch(`/api/conductores/${conductorEstado.rut}/estado`, { estado: conductorEstado.estado });
      setConductorEstado({ rut: '', estado: '' });
      cargarConductores();
    } catch (error) {
      console.error('Error al actualizar estado del conductor:', error);
    }
  };

  return (
    <div className="gestion-conductor">
      <h2>Gesti칩n de Conductores</h2>
      
      <div className="seccion">
        <h3>Conductores</h3>
        <table>
          <thead>
            <tr>
              <th>RUT</th>
              <th>Nombre</th>
              <th>N칰mero</th>
              <th>Estado</th>
            </tr>
          </thead>
          <tbody>
            {conductores.map((conductor) => (
              <tr key={conductor.rut}>
                <td>{conductor.rut}</td>
                <td>{conductor.nombre}</td>
                <td>{conductor.numero}</td>
                <td>{conductor.estado}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="seccion ">
        <h3>Agregar Conductor</h3>
        <form>
          <label>RUT:</label>
          <input type="text" value={nuevoCondcutor.rut} onChange={(e) => setNuevoConductor({ ...nuevoCondcutor, rut: e.target.value })} />
          <br />
          <label>Nombre:</label>
          <input type="text" value={nuevoCondcutor.nombre} onChange={(e) => setNuevoConductor({ ...nuevoCondcutor, nombre: e.target.value })} />
          <br />
          <label>Rol:</label>
          <input type="text" value={nuevoCondcutor.rol} onChange={(e) => setNuevoConductor({ ...nuevoCondcutor, rol: e.target.value })} />
          <br />
          <label>N칰mero:</label>
          <input type="text" value={nuevoCondcutor.numero} onChange={(e) => setNuevoConductor({ ...nuevoCondcutor, numero: e.target.value })} />
          <br />
          <label>Contrase침a:</label>
          <input type="password" value={nuevoCondcutor.contrase침a} onChange={(e) => setNuevoConductor({ ...nuevoCondcutor, contrase침a: e.target.value })} />
          <br />
          <button onClick={agregarConductor}>Agregar Conductor</button>
        </form>
      </div>

      <div className="seccion">
        <h3>Actualizar Conductor</h3>
        <form>
          <label>RUT:</label>
          <input type="text" value={conductorActualizar.rut} onChange={(e) => setConductorActualizar({ ...conductorActualizar, rut: e.target.value })} />
          <br />
          <label>Nombre:</label>
          <input type="text" value={conductorActualizar.nombre} onChange={(e) => setConductorActualizar({ ...conductorActualizar, nombre: e.target.value })} />
          <br />
          <label>N칰mero:</label>
          <input type="text" value={conductorActualizar.numero} onChange={(e) => setConductorActualizar({ ...conductorActualizar, numero: e.target.value })} />
          <br />
          <button onClick={actualizarConductor}>Actualizar Conductor</button>
        </form>
      </div>

      <div className="seccion">
        <h3>Eliminar Conductor</h3>
        <form>
          <label>RUT:</label>
          <input type="text" value={conductorEliminar.rut} onChange={(e) => setConductorEliminar({ ...conductorEliminar, rut: e.target.value })} />
          <br />
          <label>Datos:</label>
          <textarea value={conductorEliminar.datos} onChange={(e) => setConductorEliminar({ ...conductorEliminar, datos: e.target.value })} />
          <br />
          <button onClick={eliminarConductor}>Eliminar Conductor</button>
        </form>
      </div>

      <div className="seccion">
        <h3>Activar/Desactivar Conductor</h3>
        <form>
          <label>RUT:</label>
          <input type="text" value={conductorEstado.rut} onChange={(e) => setConductorEstado({ ...conductorEstado, rut: e.target.value })} />
          <br />
          <label>Estado:</label>
          <select value={conductorEstado.estado} onChange={(e) => setConductorEstado({ ...conductorEstado, estado: e.target.value })}>
            <option value="">Seleccione un estado</option>
            <option value="activo">Activo</option>
            <option value="inactivo">Inactivo</option>
          </select>
          <br />
          <button onClick={actualizarEstadoConductor}>Actualizar Estado</button>
        </form>
      </div>

      <button onClick={() => handleNavigation('/admin')}>Volver al Panel</button>
    </div>
  );
};

export default GestionConductor;
