import React, { useState } from 'react';
import '../../App.css';

const GestionRutas = () => {
  const [rutas, setRutas] = useState([
    { id: 'R105', detalles: 'Detalles de la ruta R105', horaInicio: '08:00', horaLlegada: '09:30' },
    { id: 'R106', detalles: 'Detalles de la ruta R106', horaInicio: '10:00', horaLlegada: '11:30' },
    { id: 'R107', detalles: 'Detalles de la ruta R107', horaInicio: '13:00', horaLlegada: '14:30' },
  ]);

  const [rutaSeleccionada, setRutaSeleccionada] = useState(null);
  const [nuevaRuta, setNuevaRuta] = useState({ id: '', asignacion: '', horaInicio: '', horaLlegada: '' });
  const [rutaModificar, setRutaModificar] = useState({ id: '', horaInicio: '', horaLlegada: '' });
  const [rutaEliminar, setRutaEliminar] = useState('');
  const [buscarRuta, setBuscarRuta] = useState('');

  const handleVerDetalles = (ruta) => {
    setRutaSeleccionada(ruta);
  };

  const handleCrearRuta = (e) => {
    e.preventDefault();
    setRutas([...rutas, { ...nuevaRuta, detalles: `Detalles de la ruta ${nuevaRuta.id}` }]);
    setNuevaRuta({ id: '', asignacion: '', horaInicio: '', horaLlegada: '' });
  };

  const handleModificarRuta = (e) => {
    e.preventDefault();
    setRutas(rutas.map(ruta => 
      ruta.id === rutaModificar.id ? { ...ruta, ...rutaModificar } : ruta
    ));
    setRutaModificar({ id: '', horaInicio: '', horaLlegada: '' });
  };

  const handleEliminarRuta = () => {
    setRutas(rutas.filter(ruta => ruta.id !== rutaEliminar));
    setRutaEliminar('');
  };

  const handleBuscarRuta = () => {
    const rutaEncontrada = rutas.find(ruta => ruta.id === buscarRuta);
    setRutaSeleccionada(rutaEncontrada || null);
  };

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>Ver Rutas</h2>
        <table className='user-table'>
          <thead>
            <tr>
              <th className='user-th'>ID</th>
              <th className='user-th'>Hora Inicio</th>
              <th className='user-th'>Hora Llegada</th>
              <th className='user-th'>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {rutas.map((ruta) => (
              <tr key={ruta.id}>
                <td className='user-td'>{ruta.id}</td>
                <td className='user-td'>{ruta.horaInicio}</td>
                <td className='user-td'>{ruta.horaLlegada}</td>
                <td className='user-td'>
                  <button className='user-button' onClick={() => handleVerDetalles(ruta)}>Ver Detalles</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {rutaSeleccionada && (
          <div>
            <h3>Detalles de la Ruta</h3>
            <p>{rutaSeleccionada.detalles}</p>
          </div>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Buscar Ruta</h2>
        <input 
          className='user-input' 
          type="text" 
          placeholder="ID DE RUTA" 
          value={buscarRuta}
          onChange={(e) => setBuscarRuta(e.target.value)}
        />
        <button className='user-button' onClick={handleBuscarRuta}>Buscar</button>
        {rutaSeleccionada && (
          <div>
            <h3>Información de la Ruta</h3>
            <p>{rutaSeleccionada.detalles}</p>
          </div>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Crear Ruta</h2>
        <form className='user-form' onSubmit={handleCrearRuta}>
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID DE RUTA" 
            value={nuevaRuta.id}
            onChange={(e) => setNuevaRuta({...nuevaRuta, id: e.target.value})}
          />
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID ASIGNACIÓN" 
            value={nuevaRuta.asignacion}
            onChange={(e) => setNuevaRuta({...nuevaRuta, asignacion: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA INICIO" 
            value={nuevaRuta.horaInicio}
            onChange={(e) => setNuevaRuta({...nuevaRuta, horaInicio: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA LLEGADA" 
            value={nuevaRuta.horaLlegada}
            onChange={(e) => setNuevaRuta({...nuevaRuta, horaLlegada: e.target.value})}
          />
          <button className='user-button' type="submit">Agregar ruta</button>
        </form>
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Modificar Ruta</h2>
        <form className='user-form' onSubmit={handleModificarRuta}>
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID DE RUTA" 
            value={rutaModificar.id}
            onChange={(e) => setRutaModificar({...rutaModificar, id: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA INICIO" 
            value={rutaModificar.horaInicio}
            onChange={(e) => setRutaModificar({...rutaModificar, horaInicio: e.target.value})}
          />
          <input 
            className='user-input' 
            type="time" 
            placeholder="HORA LLEGADA" 
            value={rutaModificar.horaLlegada}
            onChange={(e) => setRutaModificar({...rutaModificar, horaLlegada: e.target.value})}
          />
          <button className='user-button' type="submit">Actualizar Ruta</button>
        </form>
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Eliminar Ruta</h2>
        <input 
          className='user-input ' 
          type="text" 
          placeholder="ID DE RUTA" 
          value={rutaEliminar}
          onChange={(e) => setRutaEliminar(e.target.value)}
        />
        <button className='user-button' onClick={handleEliminarRuta}>Eliminar ruta</button>
      </section>
    </div>
  );
};

export default GestionRutas;
