import React, { useState } from 'react';
import '../../App.css';

const EditarPerfil = () => {
  const [perfil, setPerfil] = useState({
    rut: '15452684-9',
    nombre: 'PERSONA1',
    apellido: 'APELLIDO1',
    numero: '974859214',
  });

  const [editando, setEditando] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerfil(prevPerfil => ({
      ...prevPerfil,
      [name]: value,
    }));
  };

  const handleGuardarCambios = () => {
    console.log('Perfil actualizado:', perfil);
    setEditando(false);
  };

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>MI PERFIL</h2>
        {!editando ? (
          <div>
            <p><strong>RUT:</strong> {perfil.rut}</p>
            <p><strong>Nombre:</strong> {perfil.nombre}</p>
            <p><strong>Apellido:</strong> {perfil.apellido}</p>
            <p><strong>Número:</strong> {perfil.numero}</p>
            <button className='user-button' onClick={() => setEditando(true)}>Editar Perfil</button>
          </div>
        ) : (
          <form className='user-form'>
            <p><strong>RUT:</strong> {perfil.rut}</p>
            <input
              className='user-input'
              type="text"
              name="nombre"
              value={perfil.nombre}
              onChange={handleInputChange}
              placeholder="Nombre"
            />
            <input
              className='user-input'
              type="text"
              name="apellido"
              value={perfil.apellido}
              onChange={handleInputChange}
              placeholder="Apellido"
            />
            <input
              className='user-input'
              type="text"
              name="numero"
              value={perfil.numero}
              onChange={handleInputChange}
              placeholder="Número de teléfono"
            />
            <button className='user-button' onClick={handleGuardarCambios}>Guardar Cambios</button>
            <button className='user-button' onClick={() => setEditando(false)}>Cancelar</button>
          </form>
        )}
      </section>
    </div>
  );
};

export default EditarPerfil;
