import React, { useContext, useState } from 'react';
import { AuthContext } from '../context/authContext';
import Logo from './logo';
import '../App.css';

const GestionIncidencias = () => {
  const { setAuth } = useContext(AuthContext);
  const [incidencias, setIncidencias] = useState([
    { id: 'I105', detalles: 'Detalles de la incidencia I105', estado: 'Pendiente', fecha: '2023-07-01' },
    { id: 'I106', detalles: 'Detalles de la incidencia I106', estado: 'En Proceso', fecha: '2023-07-02' },
    { id: 'I107', detalles: 'Detalles de la incidencia I107', estado: 'Resuelto', fecha: '2023-07-03' },
  ]);

  const [incidenciaSeleccionada, setIncidenciaSeleccionada] = useState(null);
  const [nuevaIncidencia, setNuevaIncidencia] = useState({ id: '', idAsignacion: '', rut: '', descripcion: '', estado: '', fecha: '' });
  const [incidenciaActualizar, setIncidenciaActualizar] = useState({ id: '', estado: '' });
  const [incidenciaEliminar, setIncidenciaEliminar] = useState('');

  const handleVerDetalles = (incidencia) => {
    setIncidenciaSeleccionada(incidencia);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNuevaIncidencia(prev => ({ ...prev, [name]: value }));
  };

  const handleCrearIncidencia = (e) => {
    e.preventDefault();
    const nuevaIncidenciaConEstado = { ...nuevaIncidencia, estado: 'Pendiente' };
    setIncidencias([...incidencias, nuevaIncidenciaConEstado]);
    setNuevaIncidencia({ id: '', idAsignacion: '', rut: '', descripcion: '', estado: '', fecha: '' });
  };

  const handleBuscarIncidencia = () => {
    const incidenciaEncontrada = incidencias.find(inc => inc.id === incidenciaActualizar.id);
    setIncidenciaSeleccionada(incidenciaEncontrada || null);
  };

  const handleActualizarIncidencia = (e) => {
    e.preventDefault();
    setIncidencias(incidencias.map(inc => 
      inc.id === incidenciaActualizar.id ? { ...inc, estado: incidenciaActualizar.estado } : inc
    ));
    setIncidenciaActualizar({ id: '', estado: '' });
    setIncidenciaSeleccionada(null);
  };

  const handleEliminarIncidencia = () => {
    setIncidencias(incidencias.filter(inc => inc.id !== incidenciaEliminar));
    setIncidenciaEliminar('');
    setIncidenciaSeleccionada(null);
  };

  return (
    <div className='user-container'>
      <Logo />
      <section className='user-section'>
        <h2 className='user-title'>Ver Incidencias</h2>
        <table className='user-table'>
          <thead>
            <tr>
              <th className='user-th'>ID</th>
              <th className='user-th'>Estado</th>
              <th className='user-th'>Fecha</th>
              <th className='user-th'>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {incidencias.map((incidencia) => (
              <tr key={incidencia.id}>
                <td className='user-td'>{incidencia.id}</td>
                <td className='user-td'>{incidencia.estado}</td>
                <td className='user-td'>{incidencia.fecha}</td>
                <td className='user-td'>
                  <button className='user-button' onClick={() => handleVerDetalles(incidencia)}>Ver Detalles</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <ul className='mobile-list'>
          {incidencias.map((incidencia) => (
            <li key={incidencia.id} className='mobile-item'>
              <div className='mobile-item-field'>
                <span className='mobile-item-label'>ID:</span> {incidencia.id}
              </div>
              <div className='mobile-item-field'>
                <span className='mobile-item-label'>Estado:</span> {incidencia.estado}
              </div>
              <div className='mobile-item-field'>
                <span className='mobile-item-label'>Fecha:</span> {incidencia.fecha}
              </div>
              <button className='user-button' onClick={() => handleVerDetalles(incidencia)}>Ver Detalles</button>
            </li>
          ))}
        </ul>
        {incidenciaSeleccionada && (
          <div>
            <h3>Detalles de la Incidencia</h3>
            <p>{incidenciaSeleccionada.detalles}</p>
          </div>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Crear Incidencia</h2>
        <form className='user-form' onSubmit={handleCrearIncidencia}>
          <input 
            className='user-input' 
            type="text" 
            name="id"
            placeholder="ID INCIDENCIA" 
            value={nuevaIncidencia.id}
            onChange={handleInputChange}
          />
          <input 
            className='user-input' 
            type="text" 
            name="idAsignacion"
            placeholder="ID ASIGNACIÓN" 
            value={nuevaIncidencia.idAsignacion}
            onChange={handleInputChange}
          />
          <input 
            className='user-input' 
            type="text" 
            name="rut"
            placeholder="RUT USUARIO" 
            value={nuevaIncidencia.rut}
            onChange={handleInputChange}
          />
          <textarea 
            className='user-input' 
            name="descripcion"
            placeholder="DESCRIPCIÓN"
            value={nuevaIncidencia.descripcion}
            onChange={handleInputChange}
          ></textarea>
          <input 
            className='user-input' 
            type="date" 
            name="fecha"
            placeholder="FECHA"
            value={nuevaIncidencia.fecha}
            onChange={handleInputChange}
          />
          <button className='user-button' type="submit">Crear incidencia</button>
        </form>
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Actualizar Incidencia</h2>
        <form className='user-form' onSubmit={handleActualizarIncidencia}>
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID INCIDENCIA" 
            value={incidenciaActualizar.id}
            onChange={(e) => setIncidenciaActualizar({...incidenciaActualizar, id: e.target.value})}
          />
          <button className='user-button' type="button" onClick={handleBuscarIncidencia}>Buscar</button>
          {incidenciaSeleccionada && (
            <>
            <textarea 
                className='user-input' 
                placeholder="DETALLE DE INCIDENCIA" 
                value={incidenciaSeleccionada.detalles}
                readOnly
              ></textarea>
              <select 
                className='user-select'
                value={incidenciaActualizar.estado}
                onChange={(e) => setIncidenciaActualizar({...incidenciaActualizar, estado: e.target.value})}
              >
                <option value="">ESTADO</option>
                <option value="Pendiente">Pendiente</option>
                <option value="En Proceso">En Proceso</option>
                <option value="Resuelto">Resuelto</option>
              </select>
              <button className='user-button' type="submit">Actualizar estado</button>
            </>
          )}
        </form>
      </section>

      <section className='user-section'>
        <h2 className='user-title'>Eliminar Incidencia</h2>
        <form className='user-form'>
          <input 
            className='user-input' 
            type="text" 
            placeholder="ID INCIDENCIA" 
            value={incidenciaEliminar}
            onChange={(e) => setIncidenciaEliminar(e.target.value)}
          />
          <button className='user-button' type="button" onClick={handleBuscarIncidencia}>Buscar</button>
          {incidenciaSeleccionada && (
            <>
              <textarea 
                className='user-input' 
                placeholder="DETALLE DE INCIDENCIA" 
                value={incidenciaSeleccionada.detalles}
                readOnly
              ></textarea>
              <button className='user-button' type="button" onClick={handleEliminarIncidencia}>Eliminar Incidencia</button>
            </>
          )}
        </form>
      </section>

      <button className='user-button' onClick={() => {/* Lógica para volver al panel principal */}}>
        VOLVER AL PANEL
      </button>
    </div>
  );
};

export default GestionIncidencias;
