import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/authContext'; // Ajusta la ruta según tu estructura

const CrearIncidencia = ({ vehiculoId, onIncidenciaCreada }) => {
  const [descripcion, setDescripcion] = useState('');
  const { user } = useContext(AuthContext);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('/api/incidencias', {
        descripcion,
        vehiculo_id: vehiculoId
      }, {
        headers: { 
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json'
        }
      });

      setDescripcion('');
      if (onIncidenciaCreada) {
        onIncidenciaCreada(response.data);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Error al crear la incidencia');
    }
  };

  return (
    <div className="crear-incidencia">
      <h3>Crear Nueva Incidencia</h3>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <textarea
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          placeholder="Describe la incidencia"
          required
        />
        <button type="submit">Crear Incidencia</button>
      </form>
    </div>
  );
};

export default CrearIncidencia;
