import React, { useContext } from 'react';
import { AuthContext } from '../../context/authContext';
import Logo from '../logo';

const BienvenidoPasajero = () => {
  const { setAuth } = useContext(AuthContext);

  const handleLogout = () => {
    setAuth(null);
  };

  const styles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
      textAlign: 'center' ,
    },
    title: {
      backgroundColor: '#4a5d23',
      color: 'white',
      padding: '10px',
      marginBottom: '20px',
    },
    optionsContainer: {
      backgroundColor: '#ffe4b5',
      padding: '20px',
      borderRadius: '5px',
      marginBottom: '20px',
    },
    question: {
      backgroundColor: '#d2b48c',
      padding: '10px',
      marginBottom: '20px',
    },
    button: {
      width: '200px',
      padding: '10px',
      margin: '10px 0',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '16px',
    },
    editarPerfil: {
      backgroundColor: '#9b8250',
      color: 'white',
    },
    asignacion: {
      backgroundColor: '#d7a9a9',
      color: 'white',
    },
    incidencia: {
      backgroundColor: '#9b8250',
      color: 'white',
    },
    cerrarSesion: {
      backgroundColor: '#d3d3d3',
      color: 'black',
    },
  };

  return (
    <div>
      < Logo/>
    <div style={styles.container}>
      
      <h1 style={styles.title}>BIENVENIDO PASAJERO</h1>
      <div style={styles.optionsContainer}>
        <h2 style={styles.question}>¿QUÉ DESEAS HACER?</h2>
        <button style={{...styles.button, ...styles.editarPerfil}}>Editar Perfil</button>
        <button style={{...styles.button, ...styles.asignacion}}>Asignación</button>
        <button style={{...styles.button, ...styles.incidencia}}>Incidencia</button>
      </div>
      <button style={{...styles.button, ...styles.cerrarSesion}} onClick={handleLogout}>
        CERRAR SESIÓN
      </button>
    </div>
    </div>
  );
};

export default BienvenidoPasajero;
