import React, { useState } from 'react';
import Logo from './logo';
import '../App.css'

export default function ControlPanel() {
  const [activeTab, setActiveTab] = useState('Conductor');

  const navItems = ['Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto'];

  const drivers = [
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '984632256', estado: 'ACTIVO' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '985468970', estado: 'NO ACTIVO' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '984461863', estado: 'NO ACTIVO' },
  ];

  const passengers = [
    { rut: '17665368-9', nombre: 'PERSONA 1', numero: '', estado: '' },
    { rut: '17800268-9', nombre: 'PERSONA 2', numero: '', estado: '' },
    { rut: '17859268-9', nombre: 'PERSONA 3', numero: '', estado: '' },
  ];

  return (
    <div className="control">
      <header className="header">
        <Logo />
        <nav className="nav">
          {navItems.map((item) => (
            <button
              key={item}
              className={`nav-button ${activeTab === item ? 'active' : ''}`}
              onClick={() => setActiveTab(item)}
            >
              {item}
            </button>
          ))}
          <button className="logout-button">CERRAR SESION</button>
        </nav>
      </header>

      <main className="main-content">
        <div className="content-box">
          <h1 className="content-title">PANEL DE CONdsffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffTROL - {activeTab}</h1>
          
          { /* Aquí va el contenido según la pestaña activa */ }
        </div>
    
      </main>
    </div>
  );
}
