import React, { useContext } from 'react';
import { AuthContext } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import Logo from '../logo';
import '../../App.css'; // Asegúrate de que los estilos ahora estén en este archivo

const PanelDeControl = () => {
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    setAuth(null);
    navigate('/'); // Redirige a la página de inicio
  };

  const handleNavigation = (path) => {
    navigate(path); // Redirige a la ruta que corresponda
  };

  return (
    <div className="panel-container">
      <Logo />
      <main className="main-content">
        <div className="control-panel">
          <h2 className="panel-title">Panel de control</h2>

          <div className="management-options">
            <h3 className="options-title">¿A QUÉ GESTIÓN DESEAS INGRESAR?</h3>

            <div className="button-container">
              <button
                type="button"
                className="control-button conductor-button"
                onClick={() => handleNavigation('admin/conductor')}
              >
                Conductor
              </button>
              <button
                type="button"
                className="control-button pasajero-button"
                onClick={() => handleNavigation('admin/pasajero')}
              >
                Pasajero
              </button>
              <button
                type="button"
                className="control-button conductor-button"
                onClick={() => handleNavigation('admin/rutas')}
              >
                Rutas
              </button>
              <button
                type="button"
                className="control-button pasajero-button"
                onClick={() => handleNavigation('admin/incidencias')}
              >
                Incidencias
              </button>
              <button
                type="button"
                className="control-button conductor-button"
                onClick={() => handleNavigation('admin/asignacion')}
              >
                Asignación
              </button>
            </div>
          </div>

          <button className="logout-button" onClick={handleLogout}>
            CERRAR SESIÓN
          </button>
        </div>
      </main>
    </div>
  );
};

export default PanelDeControl;
