import React, { useState } from 'react';
import '../App.css';
import Logo from './logo'

const ControlPanel = () => {
    const [activeTab, setActiveTab] = useState('Conductor');
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  
    const navItems = ['Resumen','Conductor', 'Pasajero', 'Rutas', 'Incidencias', 'Asignación', 'Auto'];
  
    const conductorData = [
      { rut: '17665368-9', nombre: 'PERSONA 1', numero: '986432156', estado: 'ACTIVO' },
      { rut: '17800268-9', nombre: 'PERSONA 2', numero: '985466970', estado: 'NO ACTIVO' },
      { rut: '17859268-9', nombre: 'PERSONA 3', numero: '984461863', estado: 'NO ACTIVO' },
    ];
  
    const pasajeroData = [
      { rut: '17665368-9', nombre: 'PERSONA 1', numero: '', estado: '' },
      { rut: '17800268-9', nombre: 'PERSONA 2', numero: '', estado: '' },
      { rut: '17859268-9', nombre: 'PERSONA 3', numero: '', estado: '' },
    ];
  
    const renderTable = () => {
      const data = activeTab === 'Conductor' ? conductorData : pasajeroData;
      return (
        <table className="table">
          <thead>
            <tr>
              <th>RUT</th>
              <th>NOMBRE</th>
              <th>NÚMERO</th>
              <th>ESTADO</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.rut}>
                <td>{item.rut}</td>
                <td>{item.nombre}</td>
                <td>{item.numero}</td>
                <td>{item.estado}</td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    };
  
    return (
        <div>
            <Logo/>
       
    
      <div className="control-panel-container">
        <button
          className="menu-toggle"
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        >
          &#9776;
        </button>
        <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
          {navItems.map((item) => (
            <div
              key={item}
              className={`nav-item ${activeTab === item ? 'active' : ''}`}
              onClick={() => setActiveTab(item)}
            >
              {item}
            </div>
          ))}
          <button className="close-session">Cerrar Sesión</button>

        </div>
        <div className="content">
          <div className="header">
            <h1>PANEL DE CONTROL</h1>
          </div>
          <h2>{activeTab}</h2>
          {(activeTab === 'Conductor' || activeTab === 'Pasajero') && renderTable()}
          {activeTab !== 'Conductor' && activeTab !== 'Pasajero' && (
            <p>Contenido para {activeTab} no implementado aún.</p>
          )}
        </div>
      </div>
      </div>
    );
  };
  
  export default ControlPanel;
