import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './context/authContext';
import PrivateRoute from './components/privateRoute';
import './App.css';
import Inicio from './components/inicio';
import AdminDashboard from './components/adminDashboard';
import ConductorDashboard from './components/conductorDashboard';
import PasajeroDashboard from './components/pasajeroDashboard';
import PanelControlAdmin from './components/panelControlAdmin';
import GestionConductor from './components/gestionConductor';
import GestionPasajero from './components/gestionPasajero';
import 'bootstrap/dist/css/bootstrap.min.css';

import axios from 'axios';
import GestionAsignaciones from './components/gestionAsignaciones';


axios.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = 'Bearer ' + token;
    }
    return config;
  },
  error => {
    return Promise.reject(error);
  }
);

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* PÃ¡gina principal */}
          <Route path="/" element={<Inicio />} />

          {/* Rutas protegidas */}
          <Route element={<PrivateRoute allowedRoles={['admin']} />}>
            <Route path="/admin" element={<PanelControlAdmin />} />
          </Route>


            <Route path="/admin/conductor" element={<GestionConductor />} />
            <Route path="/admin/pasajero" element={<GestionPasajero />} />
            <Route path="/admin/asignaciones" element={<GestionAsignaciones />} />


          <Route element={<PrivateRoute allowedRoles={['conductor']} />}>
            <Route path="/conductor" element={<ConductorDashboard />} />
          </Route>

          <Route element={<PrivateRoute allowedRoles={['pasajero']} />}>
            <Route path="/pasajero" element={<PasajeroDashboard />} />
          </Route>
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
