import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/authContext';
import axios from 'axios';

const Incidencias = ({ vehiculoId }) => {
  const [incidencias, setIncidencias] = useState([]);
  const [descripcion, setDescripcion] = useState('');
  const { user } = useContext(AuthContext);

  useEffect(() => {
    cargarIncidencias();
  }, []);

  const cargarIncidencias = async () => {
    try {
      const response = await axios.get('/api/incidencias/usuario', {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setIncidencias(response.data);
    } catch (error) {
      console.error('Error al cargar incidencias:', error);
    }
  };

  const handleAgregarIncidencia = async () => {
    if (descripcion.trim()) {
      try {
        const response = await axios.post('/api/incidencias', {
          descripcion,
          vehiculo_id: vehiculoId
        }, {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        setIncidencias([response.data, ...incidencias]);
        setDescripcion('');
      } catch (error) {
        console.error('Error al crear incidencia:', error);
      }
    }
  };

  return (
    <div className='incidencias-container'>
      <h3>Incidencias</h3>
      <div className='incidencias-form'>
        <textarea
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          placeholder='Descripción de la incidencia'
          rows='4'
          style={{ width: '100%', marginBottom: '10px' }}
        />
        <button className='profile-button' onClick={handleAgregarIncidencia}>
          Agregar Incidencia
        </button>
      </div>
      <h4>Lista de Incidencias</h4>
      <table className='incidencias-table'>
        <thead>
          <tr>
            <th>ID</th>
            <th>Descripción</th>
            <th>Estado</th>
            <th>Usuario</th> {/* Nueva columna para el usuario */}
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {incidencias.map((incidencia) => (
            <tr key={incidencia._id}>
              <td>{incidencia._id}</td>
              <td>{incidencia.descripcion}</td>
              <td>{incidencia.estado}</td>
              <td>{incidencia.createdBy.name || 'Desconocido'}</td> {/* Mostrar nombre del usuario */}
              <td>{new Date(incidencia.createdAt).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Incidencias;
