import React, { useState } from 'react';
import '../../App.css';

const EditarPerfil = () => {
  const [perfil, setPerfil] = useState({
    rut: '15452684-9',
    nombre: 'PERSONA1',
    apellido: 'APELLIDO1',
    numero: '974859214',
  });

  const [editando, setEditando] = useState(false);
  const [rutaAsignada, setRutaAsignada] = useState({
    id: 'R001',
    origen: 'Punto A',
    destino: 'Punto B',
    pasajeros: [
      { nombre: 'Juan', apellido: 'Pérez', numero: '123456789', direccion: 'Calle 1, Ciudad' },
      { nombre: 'María', apellido: 'González', numero: '987654321', direccion: 'Calle 2, Ciudad' },
    ]
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerfil(prevPerfil => ({
      ...prevPerfil,
      [name]: value,
    }));
  };

  const handleGuardarCambios = () => {
    console.log('Perfil actualizado:', perfil);
    setEditando(false);
  };

  const abrirMapa = (direccion) => {
    const url = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(direccion)}`;
    window.open(url, '_blank');
  };

  return (
    <div className='user-container'>
      <section className='user-section'>
        <h2 className='user-title'>MI PERFIL</h2>
        {!editando ? (
          <div>
            <p><strong>RUT:</strong> {perfil.rut}</p>
            <p><strong>Nombre:</strong> {perfil.nombre}</p>
            <p><strong>Apellido:</strong> {perfil.apellido}</p>
            <p><strong>Número:</strong> {perfil.numero}</p>
            <button className='user-button' onClick={() => setEditando(true)}>Editar Perfil</button>
          </div>
        ) : (
          <form className='user-form'>
            <p><strong>RUT:</strong> {perfil.rut}</p>
            <input
              className='user-input'
              type="text"
              name="nombre"
              value={perfil.nombre}
              onChange={handleInputChange}
              placeholder="Nombre"
            />
            <input
              className='user-input'
              type="text"
              name="apellido"
              value={perfil.apellido}
              onChange={handleInputChange}
              placeholder="Apellido"
            />
            <input
              className='user-input'
              type="text"
              name="numero"
              value={perfil.numero}
              onChange={handleInputChange}
              placeholder="Número de teléfono"
            />
            <button className='user-button' onClick={handleGuardarCambios}>Guardar Cambios</button>
            <button className='user-button' onClick={() => setEditando(false)}>Cancelar</button>
          </form>
        )}
      </section>

      <section className='user-section'>
        <h2 className='user-title'>RUTA ASIGNADA</h2>
        <p><strong>ID de Ruta:</strong> {rutaAsignada.id}</p>
        <p><strong>Origen:</strong> {rutaAsignada.origen}</p>
        <p><strong>Destino:</strong> {rutaAsignada.destino}</p>
        
        <h3>Pasajeros:</h3>
        <ul className='user-list'>
          {rutaAsignada.pasajeros.map((pasajero, index) => (
            <li key={index} className='user-list-item'>
              <p><strong>Nombre:</strong> {pasajero.nombre} {pasajero.apellido}</p>
              <p><strong>Número:</strong> {pasajero.numero}</p>
              <p><strong>Dirección:</strong> {pasajero.direccion}</p>
              <button className='user-button' onClick={() => abrirMapa(pasajero.direccion)}>Ver en Mapa</button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default EditarPerfil;
