import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/authContext'; // Asumiendo que tienes un contexto de autenticación
import '../App.css';

const Incidencias = () => {
  const { user } = useContext(AuthContext); // Para obtener el rol del usuario
  const [incidencias, setIncidencias] = useState([]);
  const [nuevaIncidencia, setNuevaIncidencia] = useState({
    descripcion: '',
    vehiculo_id: ''
  });
  const [vehiculos, setVehiculos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Obtener incidencias del usuario
  const obtenerIncidencias = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/incidencias/usuario');
      setIncidencias(response.data);
      setError(null);
    } catch (error) {
      setError('Error al cargar las incidencias');
      console.error('Error al obtener incidencias:', error);
    } finally {
      setLoading(false);
    }
  };

  // Obtener vehículos según el rol
  const obtenerVehiculos = async () => {
    try {
      let response;
      if (user.role === 'conductor') {
        // Obtener solo los vehículos asignados al conductor
        response = await axios.get('/api/vehiculos/conductor');
      } else {
        // Para pasajeros, obtener todos los vehículos disponibles
        response = await axios.get('/api/vehiculos/disponibles');
      }
      setVehiculos(response.data);
    } catch (error) {
      console.error('Error al obtener vehículos:', error);
    }
  };

  useEffect(() => {
    obtenerIncidencias();
    obtenerVehiculos();
  }, [user.role]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNuevaIncidencia(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/incidencias', nuevaIncidencia);
      setNuevaIncidencia({
        descripcion: '',
        vehiculo_id: ''
      });
      obtenerIncidencias();
    } catch (error) {
      setError('Error al crear la incidencia');
      console.error('Error al crear incidencia:', error);
    }
  };

  const formatearFecha = (fecha) => {
    return new Date(fecha).toLocaleDateString('es-ES', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Obtener el texto apropiado según el rol
  const getTituloSeccion = () => {
    return user.role === 'conductor' 
      ? 'Reportar Incidencia de Vehículo' 
      : 'Reportar Incidencia de Viaje';
  };

  if (loading) {
    return <div className="user-container">Cargando...</div>;
  }

  return (
    <div className="user-container">
      {/* Sección para crear nueva incidencia */}
      <section className="user-section">
        <h2 className="user-title">{getTituloSeccion()}</h2>
        <form className="user-form" onSubmit={handleSubmit}>
          <select
            className="user-select"
            name="vehiculo_id"
            value={nuevaIncidencia.vehiculo_id}
            onChange={handleInputChange}
            required
          >
            <option value="">
              {user.role === 'conductor' 
                ? 'Seleccione su vehículo' 
                : 'Seleccione el vehículo del viaje'}
            </option>
            {vehiculos.map(vehiculo => (
              <option key={vehiculo._id} value={vehiculo._id}>
                {vehiculo.patente} - {vehiculo.modelo}
              </option>
            ))}
          </select>
          
          <textarea
            className="user-input user-textarea"
            name="descripcion"
            value={nuevaIncidencia.descripcion}
            onChange={handleInputChange}
            placeholder={user.role === 'conductor' 
              ? 'Describa el problema del vehículo' 
              : 'Describa el incidente durante el viaje'}
            required
            rows="4"
          />
          
          <button type="submit" className="user-button">
            Enviar Reporte
          </button>
        </form>
      </section>

      {/* Sección para mostrar las incidencias */}
      <section className="user-section">
        <h2 className="user-title">
          {user.role === 'conductor' ? 'Mis Reportes de Vehículos' : 'Mis Reportes de Viajes'}
        </h2>
        
        {error && <div className="user-error">{error}</div>}

        {/* Tabla para pantallas grandes */}
        <table className="user-table">
          <thead>
            <tr>
              <th className="user-th">Fecha</th>
              <th className="user-th">
                {user.role === 'conductor' ? 'Vehículo' : 'Vehículo del Viaje'}
              </th>
              <th className="user-th">Descripción</th>
              <th className="user-th">Estado</th>
            </tr>
          </thead>
          <tbody>
            {incidencias.map(incidencia => (
              <tr key={incidencia._id}>
                <td className="user-td">{formatearFecha(incidencia.createdAt)}</td>
                <td className="user-td">{incidencia.vehiculo_id?.patente}</td>
                <td className="user-td">{incidencia.descripcion}</td>
                <td className={`user-td estado-${incidencia.estado}`}>
                  {incidencia.estado}
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Lista para móviles */}
        <ul className="mobile-list">
          {incidencias.map(incidencia => (
            <li key={incidencia._id} className="mobile-item">
              <div className="mobile-item-field">
                <span className="mobile-item-label">Fecha: </span>
                {formatearFecha(incidencia.createdAt)}
              </div>
              <div className="mobile-item-field">
                <span className="mobile-item-label">
                  {user.role === 'conductor' ? 'Vehículo: ' : 'Vehículo del Viaje: '}
                </span>
                {incidencia.vehiculo_id?.patente}
              </div>
              <div className="mobile-item-field">
                <span className="mobile-item-label">Descripción: </span>
                {incidencia.descripcion}
              </div>
              <div className="mobile-item-field">
                <span className="mobile-item-label">Estado: </span>
                <span className={`estado-${incidencia.estado}`}>
                  {incidencia.estado}
                </span>
              </div>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default Incidencias;
